INSERT INTO `clt_naae_media` VALUES (29, 5, '学校概况·Overview', '{\"id\":\"29\",\"page\":\"2\",\"title_catalog\":\"学校概况·Overview\",\"two_pic\":\"\\/uploads\\/20190117\\/naae\\/8f8875abb5109cbcfc67ace4973f74de.png\",\"z_d_y_data\":\"{\\\"建校时间\\\":\\\"1947年\\\",\\\"学生人数\\\":\\\"614人\\\",\\\"占地面积\\\":\\\"32英亩\\\",\\\"宗教背景\\\":\\\"天主教\\\",\\\"周边城市\\\":\\\"旧金山\\\",\\\"学校性质\\\":\\\"男女合校\\\",\\\"年级设置\\\":\\\"9-112\\\",\\\"招生年级\\\":\\\"9-12年级\\\",\\\"住宿类型\\\":\\\"寄宿家庭\\\",\\\"师生比例\\\":\\\"1：16\\\",\\\"国际学生 \\\":\\\"10%\\\",\\\"课堂规模\\\":\\\"15\\\",\\\"高级教师\\\":\\\"75%以上硕士学位\\\",\\\"课后辅导\\\":\\\"有\\\",\\\"SAT均分\\\":\\\"1130\\\",\\\"AP课程\\\":\\\"14\\\",\\\"入学要求\\\":\\\"TOEFL\\\",\\\"申请费\\\":\\\"75美金\\\",\\\"学校官网\\\":\\\"https:\\\\\\/\\\\\\/garces.org\\\\\\/academics\\\\\\/\\\"}\"}', 2, 1547027201);
INSERT INTO `clt_naae_media` VALUES (30, 5, '学校排名·School Ranking', '{\"id\":\"30\",\"pics_3\":\"[\\\"\\/uploads\\/20190118\\/naae\\/c891cdb19c334b7fab502016d996041a.png\\\",\\\"\\/uploads\\/20190118\\/naae\\/234b32f22b07ac6de434a82522f56855.png\\\"]\",\"page\":\"3\",\"title_catalog\":\"学校排名·School Ranking\",\"ranking_one\":\"美国高中权威排名，Niche排名A类院校\\t\",\"ranking_two\":\"美国最好的大学预备学校前30%  \\t\",\"ranking_three\":\" 美国最好的多元化学科教学排名前20%\\t\",\"three_top_pic\":\"\\/uploads\\/20190117\\/naae\\/d02bd65100262d0114b804e86e13ce38.png\",\"three_low_pic\":\"\\/uploads\\/20190118\\/naae\\/08a5cba9a6ca69a236cb096f594e8015.png\"}', 3, 1547027382);
INSERT INTO `clt_naae_media` VALUES (31, 5, '学校认证·School Accredit', '{\"id\":\"31\",\"pics_4\":\"[\\\"\\/uploads\\/20190117\\/naae\\/226a1281739075003fd38465672fcfa4.jpg\\\",\\\"\\/uploads\\/20190117\\/naae\\/984a62d626136f7415b3ec7a460f896a.jpg\\\"]\",\"page\":\"4\",\"title_catalog\":\"学校认证·School Accredition\",\"authentication_one\":\"西部学校联盟成员\",\"authentication_two\":\"国家天主教教育委员会成员\",\"authentication_three\":\"9次获得加州校际联盟棒球冠军\",\"authentication_four\":\"10次获得加州校际联盟橄榄球冠军\",\"authentication_five\":\"2次获得加州校际联盟男子篮球冠军\",\"authentication_six\":\"3次获得加州校际联盟水球冠军\",\"four_top_pic\":\"\\/uploads\\/20190117\\/naae\\/cfd15aa4319762e0d0b6fe252e9fecf9.jpg\",\"four_right_pic\":\"\\/uploads\\/20190117\\/naae\\/0a8e419a68b6c77b4e3594a7fc9170e8.jpg\"}', 4, 1547027567);
INSERT INTO `clt_naae_media` VALUES (32, 5, '推荐理由·RecommendedReas', '{\"id\":\"32\",\"pics_5\":\"[\\\"\\/uploads\\/20190118\\/naae\\/6c0f352020a2afc67c6a5ba21ad3247b.jpg\\\",\\\"\\/uploads\\/20190118\\/naae\\/8111807c8a510537e5c0d0c3e2b75c7f.jpg\\\"]\",\"page\":\"5\",\"title_catalog\":\"推荐理由·RecommendedReasons\",\"five_text\":\"\",\"content5\":\"<p style=\\\"white-space: normal; text-indent: 2em;\\\"><span style=\\\"font-size: 14px; font-family: 宋体;\\\"><\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校学术水平优异，开设<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(79, 97, 40);\\\"><strong>14门AP<\\/strong><\\/span>课程和<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(79, 97, 40);\\\"><strong>20+荣誉<\\/strong><\\/span>课程，大学升学很好，爬藤率高，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(79, 97, 40);\\\"><strong>80%<\\/strong><\\/span>学生被美国前50大学录取，尤其是<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(79, 97, 40);\\\"><strong>加州大学系统。<\\/strong><\\/span>加州大学伯克利分校、加州大学洛杉矶分校等名校每年都会到学校进行招生讲座活动。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校的体育实力强，运动队<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(79, 97, 40);\\\"><strong>37支，20个<\\/strong><\\/span>俱乐部，所有国际生都可以参与学校所有社团以及活动，体育项目突出，获奖无数。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校对国际生关注度很高，设有<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(79, 97, 40);\\\"><strong>学术交流中心，<\\/strong><\\/span>有<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(79, 97, 40);\\\"><strong>一对一<\\/strong><\\/span>项目，一个美国学生带一个中国学生。老师会在课后对国际生进行<strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(79, 97, 40);\\\">学科辅导。<\\/span><\\/strong>&nbsp;<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校开设专业的<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(79, 97, 40);\\\"><strong>STEM课程，<\\/strong><\\/span>专门对科学，技术，工程和数学进行学科研究，通过实验和科学设计等活动学校把复杂沉闷的科学课堂变得有趣，增加学生对科学学科的兴趣。<\\/span><\\/p><p style=\\\"white-space: normal; text-indent: 2em; text-align: justify;\\\"><br\\/><span style=\\\"font-family: Calibri; font-size: 14px;\\\"><\\/span><\\/p>\"}', 5, 1547027897);
INSERT INTO `clt_naae_media` VALUES (33, 5, '学术特色·Academic Featur', '{\"id\":\"33\",\"pics_6\":\"[\\\"\\/uploads\\/20190118\\/naae\\/0c3c7042a5aaa0ab682e55184ba84118.png\\\",\\\"\\/uploads\\/20190118\\/naae\\/0b0390b26fd96f7bfd3e494b3de3cd20.jpg\\\"]\",\"page\":\"6\",\"title_catalog\":\"学术特色·Academic Features\",\"six_text\":\"\",\"content6\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">1.Niche调查中，学校学术评级大学升学预备均是<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(79, 97, 40);\\\"><strong>A级别，学校SAT均分1130，ACT28分，<\\/strong><\\/span>远高于美国平均水平（全美SAT均分1080，ACT均分23）。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"text-indent: 2em; font-family: 宋体, SimSun; font-size: 14px;\\\">2.学术实力强大，学校设有诸多荣誉课程和<span style=\\\"color: rgb(79, 97, 40);\\\"><strong>14门AP课程，<\\/strong><\\/span>每年<strong><span style=\\\"color: rgb(79, 97, 40);\\\">20%<\\/span><\\/strong>的学生进入美国前30大学，<span style=\\\"color: rgb(79, 97, 40);\\\"><strong>80%<\\/strong><\\/span>的学生进入到美国前50大学。&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<\\/span><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-indent: 2em;\\\">3.<span style=\\\"text-indent: 2em; color: rgb(79, 97, 40);\\\"><strong>STEM理工类项目<\\/strong><\\/span>（科学，技术，工程和数学）课程，计算机、<span style=\\\"text-indent: 2em; color: rgb(79, 97, 40);\\\"><strong>人工智能<\\/strong><\\/span>项目出色。开设FFA项目、PLTW课堂等，拥有工程设计入门、工程学概论、城市工程和建筑以及数字电子等特色教学资源。<\\/span><\\/p>\"}', 6, 1547028118);
