INSERT INTO `clt_naae_media` VALUES (35, 5, '课程设置·Curriculum', '{\"id\":\"35\",\"page\":\"8\",\"title_catalog\":\"课程设置·Curriculum\",\"eight_pic\":\"\\/uploads\\/20190118\\/naae\\/e0d5d25eb54900b1493420ef5713256a.jpg\",\"z_d_y_data\":\"{\\\"英语课程\\\":\\\"写作实验室~新闻学~年鉴~高级电影研究\\\",\\\"外语课\\\":\\\"西班牙和法语1~西班牙和法语2~荣誉课程西班牙和法语2~西班牙和法语3~西班牙和法语4\\\",\\\"基础课程\\\":\\\"美国电影~艺术1~艺术2~AP工作室艺术~舞蹈1~舞蹈2~爵士乐队~打击乐队~军乐队~高级乐队~戏剧课程\\\",\\\"数学课程\\\":\\\"代数1~高级代数~几何学~代数2~代数2（G）数学分析~微积分~统计学\\\",\\\"荣誉课程\\\":\\\"西班牙和法语2~几何~代数2~数学分析\\\",\\\"AP课程\\\":\\\"英语基础~英语文学~法国语言文学~西班牙语言~西班牙文学~美国历史~欧洲历史~微积分AB~统计学~音乐理论~工作室艺术~生物~化学~物理~计算机科学\\\"}\"}', 8, 1547028586);
INSERT INTO `clt_naae_media` VALUES (36, 5, '体育特色·Sports Activtie', '{\"pics_9_top\":\"[\\\"\\/uploads\\/20190118\\/naae\\/8a26ddc795f35cda8d62f5f97cc1725f.jpg\\\",\\\"\\/uploads\\/20190118\\/naae\\/0dbbf299ce53ccd035253b4ddd120fb4.jpg\\\"]\",\"pics_9_low\":\"[\\\"\\/uploads\\/20190118\\/naae\\/ca676b94846eed4252724015b6700042.jpg\\\",\\\"\\/uploads\\/20190118\\/naae\\/26cf9e65da34ffc9536222d75159be77.jpg\\\"]\",\"id\":\"36\",\"pics_9\":\"\",\"page\":\"9\",\"title_catalog\":\"体育特色·Sports Activties\",\"z_d_y_data\":\"{\\\"体育活动\\\":\\\"棒球，篮球，冰球，潜水，游泳，滑板，橄榄球，保龄球，登山，田径，自行车，高尔夫球，水球，足球，网球，排球，举重，摔跤等。\\\",\\\"比赛\\\":\\\"校队的网球在2016年秋获得JV网球大奖；有一名队员获得最有价值球员称号；多次获得加州锦标赛冠军\\\"}\"}', 9, 1547028892);
INSERT INTO `clt_naae_media` VALUES (37, 5, '艺术特色·Fine Atrs', '{\"id\":\"37\",\"pics_10\":\"[\\\"\\/uploads\\/20190118\\/naae\\/08594d2381e658a3f93da5e07ca826b1.jpg\\\",\\\"\\/uploads\\/20190118\\/naae\\/e87778a201444a17a795e3db0db23685.jpg\\\"]\",\"page\":\"10\",\"title_catalog\":\"艺术特色·Fine Atrs\",\"ten_pic\":\"\\/uploads\\/20190118\\/naae\\/1551f86ef63864bbdeb403470912a556.jpg\",\"z_d_y_data\":\"{\\\"舞蹈方面\\\":\\\"加尔塞斯高中的舞蹈老师通过各种教学技巧教授学生舞蹈艺术创作，为学生们提供了接触舞蹈元素、学习舞蹈技术和历史的绝好机会。在这里，学生们可以学习芭蕾舞、爵士舞和现代舞等舞种。\\\",\\\"戏剧方面\\\":\\\"戏剧方面在近几年得到了蓬勃的发展，学校学生在戏剧表演、即兴创作、舞台布景、声音、灯光设计等方面皆有着一些造诣，也在未来面临着全新的挑战，而学校则为他们提供了优质的培养机会。\\\",\\\"音乐方面\\\":\\\"拥有当地最好的艺术团体，吸引着当地优秀初中的稳定生源；学校的“Black Box”大剧院正在筹建中，将会是本地最好的音乐剧场之一。学校组建了Marching Rams乐队，并且拥有交响乐队、爵士乐队和打击乐合奏团。学校学生可以通过参与大型合奏，在各种礼仪活动中发光发热。学校还会不时地组织同学们参加当地的音乐节来增强他们的技术。\\\"}\"}', 10, 1547028998);
INSERT INTO `clt_naae_media` VALUES (38, 5, '学生社团·Club Activities', '{\"id\":\"38\",\"pics_11\":\"[\\\"\\/uploads\\/20190118\\/naae\\/dc4a22f871e63267bcde65c89fbc8c62.jpg\\\",\\\"\\/uploads\\/20190118\\/naae\\/ba04638e6a18c85c6a9b9b8f6040bd9b.jpg\\\",\\\"\\/uploads\\/20190118\\/naae\\/c5f96f3c9b3b03cc938dad814879930f.jpg\\\"]\",\"page\":\"11\",\"title_catalog\":\"学生社团·Club Activities\",\"eleven_summary\":\"沙滩文化社团，自行车社，针织社，读书社，演讲社，农业社，人生权利社，互动社团，冥想社团，模拟法庭，航空模型社团，捕鱼社，善行社团，滑雪社团，共和党人俱乐部等等。\\r\\n\"}', 11, 1547029084);
INSERT INTO `clt_naae_media` VALUES (39, 5, '学校设施·Facilities', '{\"id\":\"39\",\"pics_12\":\"[\\\"\\/uploads\\/20190118\\/naae\\/695570e63f3ee9253eeb52107987ad57.png\\\",\\\"\\/uploads\\/20190118\\/naae\\/1eb7c5b860878a105e7fd8b249872bd5.png\\\",\\\"\\/uploads\\/20190118\\/naae\\/effa85e8a4334ffea087a2f4f5b976ea.png\\\"]\",\"page\":\"12\",\"title_catalog\":\"学校设施·Facilities\",\"z_d_y_data\":\"{\\\"室内\\\":\\\"图书馆，体育馆，活动中心，学术交流中心，教堂。\\\",\\\"室外\\\":\\\"综合教学楼，办公楼，音乐艺术楼，体育馆，赛罗科学楼，阁下莱迪大厅，餐厅，学生商店，8个标准网球场，棒球场，篮球场，游泳池，标准体育场。\\\"}\"}', 12, 1547029207);
INSERT INTO `clt_naae_media` VALUES (40, 5, '大学升学情况·UNIVERSITY EN', '{\"id\":\"40\",\"pics_13\":\"\",\"page\":\"13\",\"title_catalog\":\"大学升学情况·UNIVERSITY ENTRANCE STATUS\",\"thirteen_summary\":\"2018年130个毕业生获得大量名校offer：                                                                                            1个耶鲁，1个杜克，1个加州理工，2个康奈尔，4个布朗，8个斯坦福                                                                            20+UCLA，20+UCB，40+UCSD,20+USC。加尔塞斯高中学生历年来申请最多的大学如下：\",\"thirteen_pic_top\":\"\\/uploads\\/20190118\\/naae\\/8f8e9a3cd45987e349423d5ff7a9e1ff.png\",\"thirteen_pic_low\":\"\\/uploads\\/20190118\\/naae\\/160509285b019e297b393918a85a601e.jpg\"}', 13, 1547029362);
INSERT INTO `clt_naae_media` VALUES (41, 5, '社区概况·Community Profi', '{\"id\":\"41\",\"pics_14\":\"[\\\"\\/uploads\\/20190118\\/naae\\/0decaa8278e85815fb18494b916ebbf8.png\\\",\\\"\\/uploads\\/20190118\\/naae\\/a58e308cb28fb49c562814f93a594e1d.png\\\",\\\"\\/uploads\\/20190118\\/naae\\/fbe85ed6ce79f3bd9ad17b46b73fc3b5.png\\\",\\\"\\/uploads\\/20190118\\/naae\\/592d546425ab6379e820aec7664b4911.png\\\"]\",\"page\":\"14\",\"title_catalog\":\"社区概况·Community Profile\",\"fourteen_text\":\"\",\"content14\":\"<p style=\\\"text-indent: 2em; text-align: justify;\\\">加尔塞斯纪念中学位于加州的贝克斯菲尔德城市，是全国发展速度最快的城市之一。 本地有着美丽的自然风光景和较为丰富的户外活动。距离洛杉矶国际机场 2 小时车程。<\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\">全美最具多元化城市排名51位。<\\/p>\"}', 14, 1547029594);
INSERT INTO `clt_naae_media` VALUES (42, 5, '反馈·Feedback', '{\"id\":\"42\",\"pics_15\":\"[\\\"\\/uploads\\/20190118\\/naae\\/815dfae0bb2439f948a35625edbb7ac0.png\\\",\\\"\\/uploads\\/20190118\\/naae\\/0e87bee65d49685dbd5bbdd2bfc8a3ae.png\\\",\\\"\\/uploads\\/20190118\\/naae\\/492f7aafe0e37e9a6771ef8c5cac6721.png\\\",\\\"\\/uploads\\/20190118\\/naae\\/3940523f3b3e7a7172e186de5f553f2b.png\\\"]\",\"page\":\"15\",\"title_catalog\":\"反馈·Feedback\",\"fifteen_text\":\"\",\"fifteen_pic_top\":\"\\/uploads\\/20190118\\/naae\\/60cdc74e9dae2dada287813cfc57d95a.png\",\"fifteen_pic_low\":\"\\/uploads\\/20190118\\/naae\\/f2df0022cd75c01e3dae74bd6ff92cbe.jpg\",\"content15\":\"<p style=\\\"text-indent: 2em;\\\">学生及家长对学校的整体评价为四星评价，其中三星以上的评价占86%。<\\/p><p style=\\\"text-indent: 2em;\\\">88%的学生认为加尔塞斯老师对他们很有启发。<\\/p><p style=\\\"text-indent: 2em;\\\">83%学生和家长认为加尔塞斯这所学校很擅长体育运动。<\\/p><p style=\\\"text-indent: 2em;\\\">83%学生和家长对于学生的社团和俱乐部很有兴趣。<\\/p><p style=\\\"text-indent: 2em;\\\">83%的学生和家长认为加尔塞斯的校园非常安全<\\/p><p style=\\\"text-indent: 2em;\\\">88%的学生和家长认为加尔塞斯的老师在课堂上对学生关注度很高，并且能够很好的控制课堂的节奏。<\\/p><p style=\\\"text-indent: 2em;\\\"><br\\/><\\/p>\"}', 15, 1547029786);
INSERT INTO `clt_naae_media` VALUES (43, 5, '学校费用·Tuition', '{\"id\":\"43\",\"pics_16\":\"[\\\"\\/uploads\\/20190118\\/naae\\/e4c8607e520e511f1232848f4155dd07.jpg\\\",\\\"\\/uploads\\/20190118\\/naae\\/d7c0c98b893998d5ddb3b90ac7913e0f.png\\\"]\",\"page\":\"16\",\"title_catalog\":\"学校费用·Tuition\",\"sixteen_two_level_title\":\"53500美金\\/年\",\"sixteen_summary\":\"该费用包括学费、寄宿费、餐费、管理费。此报价为预估费用，每年可能会有5%-10%的涨幅，具体以invoice为准。\"}', 16, 1547029896);
