INSERT INTO `clt_naae_media` VALUES (164, 38, '学校排名·School Ranking', '{\"id\":\"164\",\"pics_3\":\"[\\\"\\/uploads\\/20190121\\/naae\\/1e6e0793262885f945504616859136f9.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/21f55f6a600af677c1841ae371e633e2.png\\\"]\",\"page\":\"3\",\"title_catalog\":\"学校排名·School Ranking\",\"ranking_one\":\"美国Niche学校权威排名A-类学校\",\"ranking_two\":\"洛杉矶地区多元化学校排名前5\",\"ranking_three\":\"加州地区多元化学校排名前10\",\"three_top_pic\":\"\\/uploads\\/20190121\\/naae\\/d87b23f07029a737830729194af1f174.png\",\"three_low_pic\":\"\\/uploads\\/20190121\\/naae\\/43fd12e26b6e08f0469b3e58d3aee08d.png\"}', 3, 1548048690);
INSERT INTO `clt_naae_media` VALUES (165, 38, '学校认证·School Accredit', '{\"id\":\"165\",\"pics_4\":\"[\\\"\\/uploads\\/20190121\\/naae\\/5eaa71729175f598924dc74e37e503a8.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/1b1b7c20a24a14f0948c358039e3e44a.png\\\"]\",\"page\":\"4\",\"title_catalog\":\"学校认证·School Accredition\",\"authentication_one\":\"获得过三个CIF奖项和七个联盟冠军\",\"authentication_two\":\"舞蹈队获得过市级、州级和国家的大奖\",\"authentication_three\":\"美国天主教教育协会认证\",\"authentication_four\":\"西部院校协会认证\",\"authentication_five\":\"美国大学录取咨询协会会员\",\"authentication_six\":\"校舞蹈队在CA舞蹈协会举办的美国舞蹈锦标赛中名列前四\",\"four_top_pic\":\"\\/uploads\\/20190121\\/naae\\/a760738a78733625d8f308bf2d7c626a.png\",\"four_right_pic\":\"\\/uploads\\/20190121\\/naae\\/49b4df810fd1e71b93741689dbfa4e88.png\"}', 4, 1548049204);
INSERT INTO `clt_naae_media` VALUES (166, 38, '推荐理由·Recommended Rea', '{\"id\":\"166\",\"pics_5\":\"[\\\"\\/uploads\\/20190121\\/naae\\/a17e92b742f7bef750388b7956156f8a.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/2b1e99957791ee7645b44f760f742fcc.JPG\\\"]\",\"page\":\"5\",\"title_catalog\":\"推荐理由·Recommended Reasons\",\"five_text\":\"\",\"content5\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">1.<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>加州第一所<\\/strong><\\/span>也是美国所有<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>天主教学校中第一所<\\/strong><\\/span>获得“美国品格教育示范校”这一殊荣的高中，教学质量高，师资力量雄厚，小班教学，能更好的关注每个学生的学习情况。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">2.毕业升学率率近<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\">100%，<\\/span>爬藤率高，每年超过<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>10%<\\/strong><\\/span>的毕业生进入康奈尔大学，加州理工学院哈佛大学，普林斯顿大学，布朗大学，哥伦比亚大学，麻省理工学院等<\\/span><span style=\\\"color: rgb(31, 73, 125);\\\"><strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-indent: 2em;\\\">超一流名校。<\\/span><\\/strong><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-indent: 2em;\\\">3.有<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-indent: 2em; color: rgb(31, 73, 125);\\\"><strong>突出的艺术社团和体育项目，<\\/strong><\\/span><\\/span><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-indent: 2em;\\\">拥有超过<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-indent: 2em; color: rgb(31, 73, 125);\\\"><strong>50个<\\/strong><\\/span>不同的艺术社团，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-indent: 2em; color: rgb(31, 73, 125);\\\"><strong>数量位列加州之冠，<\\/strong><\\/span>舞蹈队获得过市级、州级和国家的大奖；学校同时是体育强校，体育项目获得过三个CIF奖项和七个联盟冠军。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">4.<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>交通便利，<\\/strong><\\/span>学校距离洛杉矶市区<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>20分钟<\\/strong><\\/span>车程，距离加州大学洛杉矶分校15分钟车程，地理位置十分优越。毗邻电影基地好莱坞。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">5.学校帮助学生<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>成立创业者俱乐部，<\\/strong><\\/span>旨在培养学生对经济、贸易、财务、企业管理等学科的认知，不但提升学生的组织力、创造力和领导力，还进一步帮助学生大学申请。<\\/span><\\/p>\"}', 5, 1548049511);
INSERT INTO `clt_naae_media` VALUES (167, 38, '学术特色·Academic Featur', '{\"id\":\"167\",\"pics_6\":\"[\\\"\\/uploads\\/20190121\\/naae\\/5c8b5667595cdef6b169162df92e043d.jpg\\\",\\\"\\/uploads\\/20190121\\/naae\\/5ce3c128fd30d82e527c400e19628c31.png\\\"]\",\"page\":\"6\",\"title_catalog\":\"学术特色·Academic Features\",\"six_text\":\"\",\"content6\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">1.学校开设课程丰富，有<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>12门AP课程，<\\/strong><\\/span>该校AP课程通过率总体超过<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>80%，<\\/strong><\\/span>其中微积分AB和微积分BC<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>100%<\\/strong><\\/span>通过率，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>100%<\\/strong><\\/span>的学生至少有一门科目获得<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>5分<\\/strong><\\/span>的满分成绩。学校为了提高学术整体水平还开设了<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>SAT预备课程，<\\/strong><\\/span>帮助学生提高SAT考试分数。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">2.学校对国际生关注度很高。为国际学生提供<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>各个级别<\\/strong><\\/span>的英语进阶课程<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>（ESL），<\\/strong><\\/span>帮助国际生尽快适应美国学习。 学校非常注重学生的学术水平，学校会为每个学生的学习特点分别制定不同的学习计划，还专门配有会中文的老师负责国际生的学习监督，能更好的关注每个学生的学习情况。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">3.学校与全美最大的健康维护组织，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>凯撒医疗集团（Kaiser Permanente）<\\/strong><\\/span>建立了项目合作，每年会选择6-8名有志投入医护及医疗行业的学生进入医院进行项目实习。此项目至今已合作7年之久。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">4.该校还因出色的学术教育获得<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>史蒂芬·艾伦优异教学奖。<\\/strong><\\/span><\\/span><\\/p><p><br\\/><\\/p>\"}', 6, 1548049696);
INSERT INTO `clt_naae_media` VALUES (168, 38, '课程设置·Curriculum', '{\"id\":\"168\",\"page\":\"7\",\"title_catalog\":\"课程设置·Curriculum\",\"seven_pic\":\"\\/uploads\\/20190121\\/naae\\/7c316c5fdc6678a71ffe706ddcb018db.png\",\"z_d_y_data\":\"{\\\"基础课程\\\":\\\"宗教~英语~数学~社会研究~外语~科学~美术~体育\\\\\\/健康\\\",\\\"选修课\\\":\\\"心理学社会学~生理环境科学~戏剧新闻广播~舞蹈视频制作~唱诗班的乐队~乐团音乐欣赏~艺术新闻报\\\",\\\"荣誉课程\\\":\\\"荣誉世界文化~荣誉英语9~荣誉英语10~荣誉英语11~荣誉生物学~荣誉环境科学~荣誉化学~荣誉物理学~荣誉代数我~荣誉几何~荣誉代数2~荣誉微积分\\\",\\\"AP课程\\\":\\\"AP西班牙语~AP世界历史~AP世界历史~AP美国历史上~AP政府~AP英语~AP英语文学~AP生物学~AP物理B~AP统计~AP微积分AB~AP微积分BC\\\"}\"}', 7, 1548050018);
