INSERT INTO `clt_naae_media` VALUES (170, 38, '体育特色·Sports Activiti', '{\"pics_9_top\":\"[\\\"\\/uploads\\/20190121\\/naae\\/6c4e7b3bbeca378811ec4e8d6ed1e3f0.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/cf34d3806e998ec87992276b299c61fe.png\\\"]\",\"pics_9_low\":\"[\\\"\\/uploads\\/20190121\\/naae\\/b3fd9e3334c887be2de2605b9b2fce06.JPG\\\",\\\"\\/uploads\\/20190121\\/naae\\/3acbd6afab6596a63c3b2a6405af19e7.jpg\\\"]\",\"id\":\"170\",\"pics_9\":\"\",\"page\":\"9\",\"title_catalog\":\"体育特色·Sports Activities\",\"z_d_y_data\":\"{\\\"体育强校，获得过三个CIF奖项和七个联盟冠军; 舞蹈队获得过市级、州级和国家的大奖。\\\":\\\"\\\",\\\"男生项目\\\":\\\"高尔夫球，篮球，田径，棒球，舞蹈，排球，越野跑，橄榄球，足球等\\\",\\\"女生项目\\\":\\\"高尔夫球，篮球，啦啦队，舞蹈，垒球，排球，足球，田径，健身操等\\\"}\"}', 9, 1548050285);
INSERT INTO `clt_naae_media` VALUES (171, 38, '艺术特色·Fine Arts', '{\"id\":\"171\",\"pics_10\":\"[\\\"\\/uploads\\/20190121\\/naae\\/c51774eafaea64a456c2e3afd7769d4c.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/dff5e24a3ab08fb1372d0c4deba25680.png\\\"]\",\"page\":\"10\",\"title_catalog\":\"艺术特色·Fine Arts\",\"ten_pic\":\"\\/uploads\\/20190121\\/naae\\/72f9f81ccfa4d3771f40dff87c505e00.png\",\"z_d_y_data\":\"{\\\"艺术特色\\\":\\\"学校拥有超过50个不同的艺术社团，数量位列加州之冠，学校设有合唱团，爵士乐队、鼓乐队、交响乐团等；舞蹈队获得过市级、州级和国家的大奖；校舞蹈队在CA舞蹈协会举办的美国舞蹈锦标赛中取得名列前四的好成绩。\\\"}\"}', 10, 1548050361);
INSERT INTO `clt_naae_media` VALUES (172, 38, '学生社团·Club Activities', '{\"id\":\"172\",\"pics_11\":\"[\\\"\\/uploads\\/20190121\\/naae\\/1eb1396d480765fa1943d82ca1081d7f.jpg\\\",\\\"\\/uploads\\/20190121\\/naae\\/7e82a8d44a6a048ccbdda74a327537e8.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/4762a0c907f52eb1b857d7d69c53e0c8.jpg\\\"]\",\"page\":\"11\",\"title_catalog\":\"学生社团·Club Activities\",\"eleven_summary\":\"学校拥有各种各样有趣和教育性的社团，同时学校也鼓励学生通过社区服务计划回馈给整个地区的邻居。其中包括：动漫俱乐部，ASB，运动员大使团，广播记者团，合唱团，爵士乐队，校报，KOA，弦乐团，摄影俱乐部，机器人社团，视频制作组，瑜伽俱乐部，社区清洁小组，科学杯社团，马拉松训练组，医学预科小组，加州学者联盟，年刊组织等学生组织极大的丰富了学生的业余生活并增强了学生的知识实践运用能力及领导组织能力等，为今后甲请理想的大学奠定更加坚实的基础。\"}', 11, 1548050501);
INSERT INTO `clt_naae_media` VALUES (173, 38, '学校设施·Facilities', '{\"id\":\"173\",\"pics_12\":\"[\\\"\\/uploads\\/20190121\\/naae\\/9b070705c184cdf8b05bfc358ede0ab6.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/8dcc41b923dfd6723f87c486dec77e91.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/57eab7e3a894e9395f90b2ef3fe78553.png\\\"]\",\"page\":\"12\",\"title_catalog\":\"学校设施·Facilities\",\"z_d_y_data\":\"{\\\"学校设施\\\":\\\"圣杰纳维夫中学在每间教室里都有智能黑板, 两间国家级别的电脑实验室，三间科学实验室，影像制作室，快速宽带网贯通整个学校，重新装修的图书馆，现代，设备齐全的健身房，篮球场，运动场，高尔夫球场（学校周边），排球场，棒球场，垒球场，健身房。\\\"}\"}', 12, 1548050573);
INSERT INTO `clt_naae_media` VALUES (174, 38, '大学升学情况·University-En', '{\"id\":\"174\",\"pics_13\":\"\",\"page\":\"13\",\"title_catalog\":\"大学升学情况·University-Entrance Status\",\"thirteen_summary\":\"99%的毕业升学率。2018年该校毕业生申请比例最高的大学：\",\"thirteen_pic_top\":\"\\/uploads\\/20190121\\/naae\\/bf0d2bd57692ad6efe196d523515409b.png\",\"thirteen_pic_low\":\"\\/uploads\\/20190121\\/naae\\/533139a1bacc3d0b1fb653a497dd74e4.jpg\"}', 13, 1548051228);
INSERT INTO `clt_naae_media` VALUES (175, 38, '社区概况·Community profi', '{\"id\":\"175\",\"pics_14\":\"[\\\"\\/uploads\\/20190121\\/naae\\/51bea143d1196b56890ff132435d7b52.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/2d42da07aa889bb62748ca2aeeaba1ed.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/6077543f64a32e4b3b26f8989d76d407.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/efd9513626a61412901a662cfe00e2e2.jpg\\\"]\",\"page\":\"14\",\"title_catalog\":\"社区概况·Community profile\",\"fourteen_text\":\"\",\"content14\":\"<p style=\\\"line-height: normal; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">圣杰纳维夫高中在圣费尔南多谷的中心地带。到<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>洛杉矶20分钟车程。<\\/strong><\\/span>加利福尼亚州的洛杉矶，这里气候温和、阳光充足，既是个旅游胜地，也是学习的最佳场所。圣杰纳维夫高中离BeverlyHills开车只有20分钟,到UCLA需要15分钟.环球影城主题公园也只要15分钟。<\\/span><\\/p><p style=\\\"line-height: normal; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>毗邻电影基地好莱坞，<\\/strong><\\/span>很多该校就读学生的父母为好莱坞明星。地区富裕宜人，房产水平约为<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>全美平均水平的三倍；<\\/strong><\\/span>在全美适合户外运动的城市当中排名<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>前10%，<\\/strong><\\/span>同时在全美最健康居住地排名也居于<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>前10%，<\\/strong><\\/span>十分适合居住。<\\/span><\\/p>\"}', 14, 1548051643);
INSERT INTO `clt_naae_media` VALUES (176, 38, '反馈·Feedback', '{\"id\":\"176\",\"pics_15\":\"[\\\"\\/uploads\\/20190121\\/naae\\/7f0d758284fa20c4b6b830e6e8c512fd.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/505eb83adc43322d589e5ee3a859086e.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/28e3bf4e34ba46508af2e8d3466df219.png\\\",\\\"\\/uploads\\/20190121\\/naae\\/55d442d9473de0f6a294c66e395c8f42.png\\\"]\",\"page\":\"15\",\"title_catalog\":\"反馈·Feedback\",\"fifteen_text\":\"\",\"fifteen_pic_top\":\"\\/uploads\\/20190121\\/naae\\/f1491f5bb83e39432bbb9aec4fd2b8c3.png\",\"fifteen_pic_low\":\"\\/uploads\\/20190121\\/naae\\/1be1b43d2fe6ca548545d2ca3b143972.png\",\"content15\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校教学的多样性评分A+，同时学生家长对学校的多样性给予很极高的评价，对学校的艺术和体育活动情况十分满意。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学生及家长对教师授课情况和课堂效果也十分认同和肯定，学校老师关心爱护学生，课堂学术氛围浓厚。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">2018年最新5个学生评价，全部五星评价。<\\/span><\\/p>\"}', 15, 1548052228);
INSERT INTO `clt_naae_media` VALUES (177, 38, '学校费用·Tuition', '{\"id\":\"177\",\"pics_16\":\"[\\\"\\/uploads\\/20190121\\/naae\\/9ca06093f803a2492aa3df1765e7ca12.jpg\\\",\\\"\\/uploads\\/20190121\\/naae\\/ecca985c922c206ca6bc493f7d1468c6.png\\\"]\",\"page\":\"16\",\"title_catalog\":\"学校费用·Tuition\",\"sixteen_two_level_title\":\"58200美金\\/年\",\"sixteen_summary\":\"该费用包括学费、食宿费、保险费、管理费（此报价不含课后辅导及ESL费用）。此报价为预估费用，每年可能会有5%-10%的涨幅，具体以invoice为准。\"}', 16, 1548052345);
