INSERT INTO `clt_naae_media` VALUES (185, 40, '学校概况·Overview', '{\"id\":\"\",\"page\":\"2\",\"title_catalog\":\"学校概况·Overview\",\"two_pic\":\"\\/uploads\\/20190122\\/naae\\/b45c56dca4f92e523a7dacdaa76aa14f.png\",\"z_d_y_data\":\"{\\\"建校时间\\\":\\\"1960年\\\",\\\"学生人数\\\":\\\"720人\\\",\\\"占地面积\\\":\\\"60英亩\\\",\\\"宗教背景\\\":\\\"无\\\",\\\"周边城市\\\":\\\"费城、纽约\\\",\\\"学校性质\\\":\\\"男女合校\\\",\\\"年级设置\\\":\\\"K-12年级\\\",\\\"招生年级\\\":\\\"8-12年级(6-7年级需在申请前联系招生办)\\\",\\\"住宿类型\\\":\\\"寄宿家庭\\\",\\\"师生比例\\\":\\\"1:7\\\",\\\"国际学生\\\":\\\"3%\\\",\\\"课堂规模\\\":\\\"13人\\\",\\\"高级教师\\\":\\\"66%拥有高级学历，10年以上教龄\\\",\\\"ESL课程\\\":\\\"有\\\",\\\"SAT均分\\\":\\\"1290\\\",\\\"AP课程\\\":\\\"23门\\\",\\\"入学要求\\\":\\\"TOEFL\\\\\\/SSAT\\\",\\\"学校官网\\\":\\\"www.ranneyschool.org\\\\\\/page\\\",\\\"申请费\\\":\\\"150美金\\\"}\"}', 2, 1548135884);
INSERT INTO `clt_naae_media` VALUES (186, 40, '学校排名·School Ranking', '{\"id\":\"186\",\"pics_3\":\"[\\\"\\/uploads\\/20190122\\/naae\\/2de0a7a7e2de97db19cea8a7b04930cb.png\\\",\\\"\\/uploads\\/20190122\\/naae\\/6de69e0da7d5d005323dcff6a45101eb.png\\\"]\",\"page\":\"3\",\"title_catalog\":\"学校排名·School Ranking\",\"ranking_one\":\"美国权威高中排名，Niche排名A+类院校\",\"ranking_two\":\"全美最好的k-12学校第152名\",\"ranking_three\":\"私立高中全美排名前6%\",\"three_top_pic\":\"\\/uploads\\/20190122\\/naae\\/d7d9f83c4dc23e2e8ccf2bb8d8006d74.png\",\"three_low_pic\":\"\\/uploads\\/20190122\\/naae\\/c9b9907b21cffb81a5af7b77b86785b2.png\"}', 3, 1548136140);
INSERT INTO `clt_naae_media` VALUES (187, 40, '学校认证·School Accredit', '{\"id\":\"187\",\"pics_4\":\"[\\\"\\/uploads\\/20190122\\/naae\\/7170e77b56da95ad02602e49eb0742e4.jpg\\\",\\\"\\/uploads\\/20190122\\/naae\\/593f0922d644d6371db04b146984f03f.jpg\\\"]\",\"page\":\"4\",\"title_catalog\":\"学校认证·School Accreditation\",\"authentication_one\":\"美国中部各州协会(MSA)认证\",\"authentication_two\":\"新泽西校际体育协会(NJSIAA)\",\"authentication_three\":\"新泽西体育协会成员(NJSAA)\",\"authentication_four\":\"美国中部各州协会(MSA)认证\",\"authentication_five\":\"新泽西校际体育协会(NJSIAA)\",\"authentication_six\":\"新泽西体育协会成员(NJSAA)\",\"four_top_pic\":\"\\/uploads\\/20190122\\/naae\\/0b3eb88a52462eeedfc4c69c4879eb13.jpg\",\"four_right_pic\":\"\\/uploads\\/20190122\\/naae\\/eda1d25b752ad4ae6fcdbc1d2eac1928.jpg\"}', 4, 1548136557);
INSERT INTO `clt_naae_media` VALUES (188, 40, '推荐理由·Recommended Rea', '{\"id\":\"\",\"pics_5\":\"[\\\"\\/uploads\\/20190122\\/naae\\/e1266403015a285e7017b55ad16b0a1e.jpg\\\",\\\"\\/uploads\\/20190122\\/naae\\/787a1fe536923122af14022d3363cea9.jpg\\\"]\",\"page\":\"5\",\"title_catalog\":\"推荐理由·Recommended Reasons\",\"five_text\":\"\",\"content5\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">1.兰尼学校拥有近六十年的建校史，在全美最好的k-12学校排名中处于<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>前6%，<\\/strong><\\/span>是新泽西州最好的k-12学校排名的<strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\">第10名，<\\/span><\\/strong>在美国权威高中排名Niche排名中的<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>A+<\\/strong><\\/span>类院校。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">2.学校教学资源丰富，师资力量雄厚，开设了<strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\">23门AP课程；<\\/span><\\/strong>学校注重学生<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>全方位的发展，<\\/strong><\\/span>在艺术、体育等方面的培养上为学生提供了各种精良的硬件设备和配套建筑，如艺术馆、3个画廊、视觉艺术中心等，以及各种体育场馆和器械。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">3.在体育与艺术的培养上，学校带领学生取得了丰硕的成果。在文学艺术方面，该校学生曾获得过Count Basie 剧院优秀高中戏剧巴希奖、Tow River 戏剧麦德龙学者项目奖；学校的文学杂志MAG连续8年获得哥伦比亚和美国学者新闻联合会的认可和金奖。在体育运动方面，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>击剑队蹭获得州冠军，<\\/strong><\\/span><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>女子网球队曾获得NJISS锦标赛冠军。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">4.该校的机器人也是这个学校的特色项目。<strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\">机器人项目<\\/span><\\/strong>是该校<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>STEAM课程<\\/strong><\\/span>里很重要的一个分支。从4年级开始，学生就有机会接触这个项目。学生在学习编程、工程理论、建造并操控机器人的过程中得以锻炼团队合作、策略制定、电脑技术和解决问题的能力。自2010年起，兰尼的机器人团队参与了4届国际性的机器人大赛。在2014年，学校赢得<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>VEX机器人美国公开赛冠军，<\\/strong><\\/span>2015年，赢得<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>VEX机器人锦标赛新泽西州冠军。<\\/strong><\\/span>同时还获得了世界级比赛团体第12名和个人第四名的好成绩。<\\/span><\\/p>\"}', 5, 1548136804);
INSERT INTO `clt_naae_media` VALUES (189, 40, '学术特色·Academic Featur', '{\"id\":\"\",\"pics_6\":\"[\\\"\\/uploads\\/20190122\\/naae\\/37fb81a8fbb396bf5cffef632b13c4f5.jpg\\\",\\\"\\/uploads\\/20190122\\/naae\\/f29dc96d970a2f33b4b1e018b20c3ac8.jpg\\\"]\",\"page\":\"6\",\"title_catalog\":\"学术特色·Academic Features\",\"six_text\":\"\",\"content6\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">1.Niche调查中，学校学术评级大学升学预备均是<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>A+级别，学校SAT均分1290，ACT30分，<\\/strong><\\/span>较高于美国平均水平（全美SAT均分1100，ACT均分21）。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">2.学校师生比例很低，为1:7，加上小课堂的设置，让学生的受关注度很高。此外多位学生及家长评价到，学校课程安排严谨、科学，丰富的体育活动和趣味社团等让学生课内课外都能得到全面培养和发展。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">3.学校教育资源丰富，师资力量雄厚，开设23门AP课程。学校的大学升学率也十分可观，高达100%，每年都有很多学生进入美国名校及国际名校。同时学校前瞻性的为学生提供了从高一就跟进的大学学前指导，全方位帮助学生为未来的发展打好基础。<\\/span><\\/p>\"}', 6, 1548136959);
INSERT INTO `clt_naae_media` VALUES (190, 40, '课程设置·Curriculum', '{\"id\":\"190\",\"page\":\"7\",\"title_catalog\":\"课程设置·Curriculum\",\"seven_pic\":\"\\/uploads\\/20190122\\/naae\\/be75a44aebb81b6dd5b5dec4b37fe3b8.png\",\"z_d_y_data\":\"{\\\"基础课程\\\":\\\"英语~数学~科学~历史~法语~汉语~西班牙语~生物学~化学~宏观经济学~物理学~美国历史学~艺术史~乐理学~计算机科学原理~统计学~二维设计~工作室艺术\\\",\\\"艺术课程\\\":\\\"戏剧~摄影~音乐~绘画~制陶~绘图~3D打印~版画~设计~动漫~雕刻~制片~建筑学\\\"}\"}', 7, 1548137456);
INSERT INTO `clt_naae_media` VALUES (191, 40, '课程设置·Curriculum', '{\"id\":\"191\",\"page\":\"8\",\"title_catalog\":\"课程设置·Curriculum\",\"eight_pic\":\"\\/uploads\\/20190122\\/naae\\/50665fdb9e6821108fbba83b9d4a9b88.png\",\"z_d_y_data\":\"{\\\"表演艺术\\\":\\\"管弦乐~戏剧~合唱\\\",\\\"AP课程\\\":\\\"艺术史~生物学~微积分AB~微积分BC~化学~计算机科学A~英语语言作品~英语文学作品~欧洲历史学~法语语言文化~宏观经济学~微观经济学~乐理学~物理学1~西班牙语言文化~美国历史学~世界历史学~计算机原理~物理学1~统计学~二维设计~三维设计~绘画\\\"}\"}', 8, 1548137534);
INSERT INTO `clt_naae_media` VALUES (192, 40, '体育特色·Sports Activiti', '{\"pics_9_top\":\"[\\\"\\/uploads\\/20190122\\/naae\\/c53cca150dc1315c14e066577496d85a.jpg\\\",\\\"\\/uploads\\/20190122\\/naae\\/a2026c69d0c697566e76ccb2ce3a1c8a.jpg\\\"]\",\"pics_9_low\":\"[\\\"\\/uploads\\/20190122\\/naae\\/7c2ebe275a90750e87da174425f27c45.jpg\\\",\\\"\\/uploads\\/20190122\\/naae\\/5ce7ef8121baee371c342e174fabfc9d.jpg\\\"]\",\"id\":\"192\",\"pics_9\":\"\",\"page\":\"9\",\"title_catalog\":\"体育特色·Sports Activities\",\"z_d_y_data\":\"{\\\"篮球，田径，足球，陆上曲棍球，网球，棒球，高尔夫，垒球、击剑、力量训练等\\\":\\\"该校的体育团队先后获得了30多项个人和团体冠军，包括游泳，网球，篮球和击剑等。\\\"}\"}', 9, 1548137809);
INSERT INTO `clt_naae_media` VALUES (193, 40, '艺术特色·Fine Arts', '{\"id\":\"193\",\"pics_10\":\"[\\\"\\/uploads\\/20190122\\/naae\\/a72dc550385c5b594fea31248e7077fb.jpg\\\",\\\"\\/uploads\\/20190122\\/naae\\/d6b10348dccdc3e76be73d52a828128a.jpg\\\"]\",\"page\":\"10\",\"title_catalog\":\"艺术特色·Fine Arts\",\"ten_pic\":\"\\/uploads\\/20190122\\/naae\\/a931eac8ad78bca6fba5afefac390b4d.jpg\",\"z_d_y_data\":\"{\\\"艺术特色\\\":\\\"兰尼学校在对学生艺术培养上做出了巨大努力，无论是从硬件设施，还是师资团队，都为学生提供了十分先进和优良的条件。如学校专门设有展览与绘画创作的艺术馆，可进行戏剧、合唱、乐器等表演的大礼堂等；同时学校还为学生提供了最前沿的3D打印技术的学习，在视觉艺术中心，学生可尝试这一最新的科学成果，还能学习摄影等技术。\\\"}\"}', 10, 1548138116);
INSERT INTO `clt_naae_media` VALUES (194, 40, '学生社团·Club Activities', '{\"id\":\"\",\"pics_11\":\"[\\\"\\/uploads\\/20190122\\/naae\\/ea63018a31bf8325f50c07efe9d51393.jpg\\\",\\\"\\/uploads\\/20190122\\/naae\\/6a547bb19d3dbed904c91eb99591ae8d.png\\\",\\\"\\/uploads\\/20190122\\/naae\\/33e845ea980acd341ff2f1a38c022936.png\\\"]\",\"page\":\"11\",\"title_catalog\":\"学生社团·Club Activities\",\"eleven_summary\":\"学校注重学生多方面的发展，为学生设置了种类丰富、主题设计十分全面的课外活动和社团俱乐部。\\r\\n乐队，学术团队，户外俱乐部，诗歌俱乐部，机器人，阅览室，演讲与辩论，历史俱乐部，妇女研究，年鉴，针织，烧烤俱乐部，电影俱乐部,数学联赛，政治俱乐部，哈佛模拟国会，烧烤俱乐部等。\"}', 11, 1548138195);
INSERT INTO `clt_naae_media` VALUES (195, 40, '学校设施·Facilities', '{\"id\":\"195\",\"pics_12\":\"[\\\"\\/uploads\\/20190122\\/naae\\/95f9b72635158d47f074a6ec487928bf.png\\\",\\\"\\/uploads\\/20190122\\/naae\\/e11dad4cd1dff75465852bc37a1ca4fd.png\\\",\\\"\\/uploads\\/20190122\\/naae\\/e6775e974642cdde57466c0895837f9d.png\\\"]\",\"page\":\"12\",\"title_catalog\":\"学校设施·Facilities\",\"z_d_y_data\":\"{\\\"\\\":\\\"学校设施齐全，在教学方面，学校分别为低年级和初高中年级设置了不同的教学综合设施，此外还配备了艺术馆、礼堂、视觉艺术中心和多媒体中心，还分别为低年级和初高中设置了不同的图书馆；在体育运动方面，学校有综合体育馆，可进行室内篮球、游泳等训练，此外还分别有游泳馆、力量训练室、足球场、网球中心、健身房、塑胶田径跑道等。\\\"}\"}', 12, 1548138255);
INSERT INTO `clt_naae_media` VALUES (196, 40, '大学升学情况·University-En', '{\"id\":\"\",\"pics_13\":\"\",\"page\":\"13\",\"title_catalog\":\"大学升学情况·University-Entrance Status\",\"thirteen_summary\":\"学校有着100%的大学升学率，很多学生进入了美国受欢迎的大学和军事院校，以及一些著名的国际院校；该校的学生申请较多的大学有如下：\",\"thirteen_pic_top\":\"\\/uploads\\/20190122\\/naae\\/edcad31f5c4a03eaf92544e843ac635b.png\",\"thirteen_pic_low\":\"\\/uploads\\/20190122\\/naae\\/fd06313c8812037a7b8e307969e576ed.jpg\"}', 13, 1548138493);
INSERT INTO `clt_naae_media` VALUES (197, 40, '社区概况·Community Profi', '{\"id\":\"197\",\"pics_14\":\"[\\\"\\/uploads\\/20190122\\/naae\\/dd4c8080fd7a3876ca007eb765789930.png\\\",\\\"\\/uploads\\/20190122\\/naae\\/024621e4927dcbececb51f8a41873269.png\\\",\\\"\\/uploads\\/20190122\\/naae\\/905dfe072c7c877f573da8404edc9716.png\\\",\\\"\\/uploads\\/20190122\\/naae\\/aa45ed737a64fbfcc565dbe94fccc829.png\\\"]\",\"page\":\"14\",\"title_catalog\":\"社区概况·Community Profile\",\"fourteen_text\":\"\",\"content14\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">兰尼学校位于新泽西州蒙茅斯县的自治市镇廷顿瀑布，是新泽西州最好的居住地之一。该镇东部临近新泽西湾，从学校驾车到美丽的蒙默思郡海滩仅需15分钟。廷顿瀑布作为临近纽约的一个郊区，很多人选择退休后来这里养老，居民也相对平和保守。2010年人口普查该镇人口为1.79万，其中82%的居民为白人，亚裔人种占4.7%。在交通方面，驾车从学校出发，到纽约市大约需1小时，到费城大约需1小时20分钟；如果需要搭乘飞机，从学校到肯尼迪国际机场约需1小时10分钟,到纽瓦克自由国际机场约需45分钟.<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该地区在全美最安全的市郊排名中位列<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>前30%，<\\/strong><\\/span>在新泽西州最适合退休后居住地中位列<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>前6%，<\\/strong><\\/span>城市排名<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>A-级别.<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该地区人口相对较少，总人口数为1.79万；男女比例均衡，基本达到1:1；治安相对良好，犯罪案件发生数仅占到全国的3%.<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该社区居民受教育程度相对较高,获得本科及以上学历的居民占45%.<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该地区房产价值相对较低，但租房成本并未随之下降，租金约为全国的2倍左右，约有三分之一的房屋被出租.<\\/span><\\/p><p><br\\/><\\/p>\"}', 14, 1548138790);
INSERT INTO `clt_naae_media` VALUES (198, 40, '反馈·Feedback', '{\"id\":\"\",\"pics_15\":\"[\\\"\\/uploads\\/20190122\\/naae\\/4ab2a87148886dcb205e4f3f9b7fde37.png\\\",\\\"\\/uploads\\/20190122\\/naae\\/b19d4b54a08153c72bc0f2601cf998a6.png\\\"]\",\"page\":\"15\",\"title_catalog\":\"反馈·Feedback\",\"fifteen_text\":\"\",\"fifteen_pic_top\":\"\\/uploads\\/20190122\\/naae\\/0ad300058c78d01b0272bb8530669d8b.png\",\"fifteen_pic_low\":\"\\/uploads\\/20190122\\/naae\\/51eddb0f215e1199b22d9883e8880515.png\",\"content15\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">在读学生及家长整体评价为<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(54, 96, 146);\\\"><strong>4星半，其中三星以上评价占100%.<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">多位学生及家长称赞道，该学校课程设置齐备、科学，很好地满足了对学生文化课的教授和培养；同时所有老师资历深厚，十分关心和照顾学生，关注每一个学生的发展；此外，学校在社团活动、体育艺术等的建设方面也是丰富多彩，每位学生都能得到很好地兴趣开拓和提升.<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">有学生及家长评价该学校安保措施很到位，学校有日常保安站岗、校警巡逻等；学校总是及时响应社会问题，杜绝如校园暴力等顽疾在学校出现.<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">还有同学和家长评价道，学校环境优美，配套设施和后勤管理都很人性化，良好的学习氛围和学校文化让每一位同学都能很快融入到集体中，甚至从高一就开始的大学学前指导，让每位同学都能很好地选择与规划自己未来的发展方向.<\\/span><\\/p>\"}', 15, 1548139246);
INSERT INTO `clt_naae_media` VALUES (199, 40, '学校费用·Tuition', '{\"id\":\"\",\"pics_16\":\"[\\\"\\/uploads\\/20190122\\/naae\\/db0e0559cfa7f7ef7b4f243c7246487f.jpg\\\",\\\"\\/uploads\\/20190122\\/naae\\/a97a418b33566ad5c25986b432ae7319.JPG\\\"]\",\"page\":\"16\",\"title_catalog\":\"学校费用·Tuition\",\"sixteen_two_level_title\":\"80500美金\\/年\",\"sixteen_summary\":\"该费用包括学费、寄宿费、餐费、管理费。此报价为预估费用，每年可能会有5%-10%的涨幅，具体以invoice为准。（此外学生还需额外缴纳注册费、国际生费，科技费等杂费。）\"}', 16, 1548139298);
