INSERT INTO `clt_naae_media` VALUES (201, 41, '伯克曼学校', '{\"id\":\"\",\"page\":\"1\",\"title_catalog\":\"伯克曼学校\",\"two_level_title\":\"Perkiomen School\",\"form_summary\":\"学术强校，提供30门AP课程、11门荣誉课程、ESL课程、STEAM课程；小班教学，1:7的师生比率，学生受关注程度高。\",\"one_pic\":\"\\/uploads\\/20190122\\/naae\\/d1981a16166d7c7bdf8923dac885368e.jpg\"}', 1, 1548152843);
INSERT INTO `clt_naae_media` VALUES (202, 41, '学校概况·Overview', '{\"id\":\"\",\"page\":\"2\",\"title_catalog\":\"学校概况·Overview\",\"two_pic\":\"\\/uploads\\/20190122\\/naae\\/2b35ff2e26354e4bdc0d244f61709844.jpg\",\"z_d_y_data\":\"{\\\"建校时间\\\":\\\"1875年\\\",\\\"学生人数\\\":\\\"350人\\\",\\\"占地面积\\\":\\\"172英亩\\\",\\\"宗教背景\\\":\\\"无\\\",\\\"周边城市\\\":\\\"费城\\\",\\\"学校性质\\\":\\\"混校\\\",\\\"年级设置\\\":\\\"6-12年级\\\",\\\"招生年级\\\":\\\"6-11年级\\\",\\\"住宿类型\\\":\\\"寄宿\\\\\\/走读\\\",\\\"师生比例\\\":\\\"1:7\\\",\\\"国际学生\\\":\\\"30%\\\",\\\"课堂规模\\\":\\\"12人\\\",\\\"ESL课程\\\":\\\"有\\\",\\\"SAT均分\\\":\\\"1190\\\",\\\"AP课程\\\":\\\"30门\\\",\\\"入学要求\\\":\\\"TOEFL\\\",\\\"学校官网\\\":\\\"www.perkiomen.org\\\",\\\"申请费\\\":\\\"100美金\\\"}\"}', 2, 1548153267);
INSERT INTO `clt_naae_media` VALUES (203, 41, '学校排名·School Ranking', '{\"id\":\"\",\"pics_3\":\"[\\\"\\/uploads\\/20190122\\/naae\\/a964b679d6271969a9242280a2ec0ebb.png\\\",\\\"\\/uploads\\/20190122\\/naae\\/ebca2010be809648e984427fd3991f7a.png\\\"]\",\"page\":\"3\",\"title_catalog\":\"学校排名·School Ranking\",\"ranking_one\":\"美国权威高中排名，Niche排名A+ 类院校\",\"ranking_two\":\"全美私立高中排名前17%\",\"ranking_three\":\"宾州大学预备私立高中排名第32\",\"three_top_pic\":\"\\/uploads\\/20190122\\/naae\\/8657de1c0d7fb0ce8176538ba8381463.png\",\"three_low_pic\":\"\\/uploads\\/20190122\\/naae\\/845ba5065ccd78226d39a1de612d39bd.jpg\"}', 3, 1548153566);
INSERT INTO `clt_naae_media` VALUES (204, 41, '学校认证·School Accredit', '{\"id\":\"204\",\"pics_4\":\"[\\\"\\/uploads\\/20190122\\/naae\\/b7069f91b041664883be16dd5d86e74b.jpg\\\",\\\"\\/uploads\\/20190122\\/naae\\/7baf5e7d5d76a94335926e898113d9bf.jpg\\\"]\",\"page\":\"4\",\"title_catalog\":\"学校认证·School Accredition\",\"authentication_one\":\"优等生协会\",\"authentication_two\":\"国家荣誉协会\",\"authentication_three\":\"Tri-County独立学校联盟\",\"authentication_four\":\"宾夕法尼亚州独立学校体育协会\",\"authentication_five\":\"康涅狄格州发明大会成就奖\",\"authentication_six\":\"排球：TCL赛季和冠军赛冠军\",\"four_top_pic\":\"\\/uploads\\/20190122\\/naae\\/71391c2a97e6627e8bdafd1c7bfba97c.jpg\",\"four_right_pic\":\"\\/uploads\\/20190123\\/naae\\/25a71b488492de31cd6bf12edc11248f.jpg\"}', 4, 1548153690);
INSERT INTO `clt_naae_media` VALUES (205, 41, '推荐理由·Recommended Rea', '{\"id\":\"\",\"pics_5\":\"[\\\"\\/uploads\\/20190123\\/naae\\/3892eabdcfcf9b2215b0ecd09bfa55c2.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/9de7f586f7c1542c355215b820a8662f.jpg\\\"]\",\"page\":\"5\",\"title_catalog\":\"推荐理由·Recommended Reasons\",\"five_text\":\"\",\"content5\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">1.<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(95, 73, 122);\\\"><strong>师生比例为1:7,小班教学，<\\/strong><\\/span>平均每班人数为11，学生受关注程度极高。<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(95, 73, 122);\\\"><strong>95％<\\/strong><\\/span>的教师住在校园里，学生有机会与老师，顾问和导师建立深厚的关系。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">2.学校提供30个AP课程、11个荣誉课程、杰出的艺术课程和精彩的选修课。学校将<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(95, 73, 122);\\\"><strong>STEAM课程（科学，技术，工程，艺术，数学）扩展到包括机器人，火箭，水下机器人计划等，多名学生获得康涅狄格州发明大会的成就奖。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">3.<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(95, 73, 122);\\\"><strong>高科技融入教学，拥有数字艺术实验室，<\\/strong><\\/span>所有教室和宿舍都有网线，所有教室都配有电脑和ACTIVBoards以及教职员工的iPad计划、Mac实验室、 六台3D打印机、虚拟现实系统和创新中心的全套机器人、程序和技术。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">4.成立了<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(95, 73, 122);\\\"><strong>企业家学院，<\\/strong><\\/span>旨在促进创造力和构思，培养学成成为优秀的企业家。与<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(95, 73, 122);\\\"><strong>宾夕法尼亚州立大学合作，<\\/strong><\\/span>为企业家学院的学生带来无与伦比的经验，他们将学习制造，营销，演讲技巧，并受到启发，将他们的想法发展成可行的产品，服务或公司。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">5.<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(95, 73, 122);\\\"><strong>成立医学研究所，<\\/strong><\\/span>为有兴趣从事医学职业的学生提供课程作业和体验式学习。学生将有能力根据个人兴趣定制他们的计划。该计划包括传统的课程作业，外部经验和实习。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">6.学校为学生提供<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(95, 73, 122);\\\"><strong>大学升学指导，<\\/strong><\\/span>帮助学生掌握大学申请和搜索过程。该计划的重要优势在于学校辅导员能够为每个学生提供真正特别的个人关注度，为学生提供专门的大学咨询计划<\\/span><\\/p><p><br\\/><\\/p>\"}', 5, 1548206760);
INSERT INTO `clt_naae_media` VALUES (206, 41, '学术特色·Academic Featur', '{\"id\":\"\",\"pics_6\":\"[\\\"\\/uploads\\/20190123\\/naae\\/8cdd1662d3102010994a3c7ae2619eba.png\\\",\\\"\\/uploads\\/20190123\\/naae\\/e9936b3e76695abc8bc3dd6804688da3.jpg\\\"]\",\"page\":\"6\",\"title_catalog\":\"学术特色·Academic Features\",\"six_text\":\"\",\"content6\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">Niche调查中，学校学术评级是<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(95, 73, 122);\\\"><strong>A+级别，升学率为100%，学校SAT均分1190，ACT30分，<\\/strong><\\/span>远高于美国平均水平（全美SAT均分1080，ACT均分30）。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">Niche调查中，师生比例为1：7，学生受关注程度高，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(95, 73, 122);\\\"><strong>88%<\\/strong><\\/span>的学生和家长认为该学校的教师讲课生动；<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(95, 73, 122);\\\"><strong>88%<\\/strong><\\/span>的学生和家长认为该学校的教师非常关心学生；<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(95, 73, 122);\\\"><strong>75%<\\/strong><\\/span>的学生和家长认为该学校的教师能够较好掌控课堂。<\\/span><\\/p>\"}', 6, 1548206891);
INSERT INTO `clt_naae_media` VALUES (207, 41, '课程设置·Curriculum', '{\"id\":\"\",\"page\":\"7\",\"title_catalog\":\"课程设置·Curriculum\",\"seven_pic\":\"\\/uploads\\/20190123\\/naae\\/b36f83d3bdc21c85644b37766dfafafc.jpg\",\"z_d_y_data\":\"{\\\"AP课程\\\":\\\"艺术史~音乐理论~工作室艺术2D设计~工作室艺术绘图~英语语言和作文~英国文学和作文~宏观经济学~微观经济学~欧洲历史~人文地理~心理学~美国政府和政治~美国历史~世界历史~微积分AB~微积分BC~计算机科学A.~计算机科学原理~统计~生物学~化学~环境科学~物理1~物理2~物理学C电学和磁学~物理C力学~中国语言文化~法语语言和文化~德语语言和文化~拉丁文~西班牙语言和文化\\\"}\"}', 7, 1548207183);
