INSERT INTO `clt_naae_media` VALUES (211, 41, '学生社团·Club Activities', '{\"id\":\"\",\"pics_11\":\"[\\\"\\/uploads\\/20190123\\/naae\\/76f981f2e0a46b376342b56fb1ed838b.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/96a8ccd920bf94204c94c88a2580b045.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/5202db5d409c24d5dfb83f0fffc0db90.jpg\\\"]\",\"page\":\"11\",\"title_catalog\":\"学生社团·Club Activities\",\"eleven_summary\":\"领导组织：优等生协会、荣誉审查委员会、中学生参议院、演示站、考务人员、紫色钥匙俱乐部、高中学生参议院、写作研究员。\\r\\n俱乐部：艺术俱乐部、B\'nspired、烹饪美食俱乐部、三维原型、多样性俱乐部、农场俱乐部、飞盘俱乐部、同性恋直接联盟、国际俱乐部、投资俱乐部、K\'nex俱乐部、针织俱乐部、魔术俱乐部、马江俱乐部、数学俱乐部、初中读奥运、模特俱乐部、任天堂俱乐部、表演艺术俱乐部、Perkspectives（在线杂志见上文）、摄影俱乐部、诗歌俱乐部、增辉、高中阅读奥林匹克运动会、世界语言文化俱乐部。\\r\\n活动：棋盘游戏、卡片播放、大学辅导、多米诺、范洞、Godon\\/GoStop、Mandal着色、冥想、打开音乐工作室、桌面策略游戏、瑜伽、悠悠球。\"}', 11, 1548208235);
INSERT INTO `clt_naae_media` VALUES (212, 41, '学校设施·Facilities', '{\"id\":\"\",\"pics_12\":\"[\\\"\\/uploads\\/20190123\\/naae\\/e238d9c8da358ce0d92bf807af2fa770.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/50f7287ae1ddf9ad49bd8f6abdb9140c.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/2e64b79ccf95ffa14593f695da759d35.jpg\\\"]\",\"page\":\"12\",\"title_catalog\":\"学校设施·Facilities\",\"z_d_y_data\":\"{\\\"学术建筑\\\":\\\"卡内基图书馆; 霍伦巴赫学术中心：艾里森中学; Kehs Hall：艺术中心; Kriebel Hall; Robert M. Schumo学术中心; 企业家研究所创新中心。\\\",\\\"宿舍楼\\\":\\\"Duyckinck Hall; Kriebel Hall; 鲁尔霍尔; 舒尔茨霍尔; 科尔贝霍尔; Riordan Hall。\\\",\\\"校园社区建筑\\\":\\\"RobertsHall\\\\\\/Robbie\'s（学生中心\\\\\\/书店）; Creamery（校长宿舍）; 家长大厅（餐厅）; Cleaver-Kistler健康中心; 学院住房; 实体植物和地面维护建筑。\\\",\\\"校园绿地\\\":\\\"植物园，贝克花园，古德温雨园，科尔-里奥丹广场，Scherrer-Lamb花园，Schumo广场，斯特凡诺冥想花园。\\\",\\\"运动空间\\\":\\\"Huttel Natatorium; 展览馆; 练习体育馆; 贝克棒球场; Stauffer家庭网球场; Zeller Soccer＆Boys Lacrosse Field; 弗里曼足球＆女孩长曲棍球场，舒尔茨垒球场，中学女子足球场，女子足球场，中学女子长曲棍球场，中学男孩长曲棍球场，中学男孩足球场。\\\"}\"}', 12, 1548208526);
