INSERT INTO `clt_naae_media` VALUES (214, 41, '社区概况·Community Profi', '{\"id\":\"214\",\"pics_14\":\"[\\\"\\/uploads\\/20190123\\/naae\\/53c9be48916f29ad4d02c2a30d9534fb.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/a6b8bcbfa596548fd43b1f5fd658238a.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/feadd0b8d31cb7875c193926adab7a9b.png\\\",\\\"\\/uploads\\/20190123\\/naae\\/094efea96ad8d993202354aeb926dc7f.jpg\\\"]\",\"page\":\"14\",\"title_catalog\":\"社区概况·Community Profile\",\"fourteen_text\":\"\",\"content14\":\"<p style=\\\"text-indent: 2em;\\\">彭斯堡是费城的一个郊区，是沿着29号公路小城镇地带的一部分：红山，宾夕法尼亚和东格林维尔。位于费城西北40英里（64公里）处，距离Quakertown西南5英里（8.0公里）。交通便利，<span style=\\\"color: rgb(95, 73, 122);\\\"><strong>距费城及费城国际机场仅需1个小时的车程。&nbsp;<\\/strong><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\">该城市整体评分为<span style=\\\"color: rgb(95, 73, 122);\\\"><strong>4.0，<\\/strong><\\/span>获得较多居民好评。<\\/p>\"}', 14, 1548208937);
INSERT INTO `clt_naae_media` VALUES (215, 41, '反馈·Feedback', '{\"id\":\"\",\"pics_15\":\"[\\\"\\/uploads\\/20190123\\/naae\\/32466028d0e8effcd84e7cec32f5db43.png\\\",\\\"\\/uploads\\/20190123\\/naae\\/97732bf6c50dafdb06d9c3bdecab997f.png\\\",\\\"\\/uploads\\/20190123\\/naae\\/77278a1f10d8ee369f420c046b912ab8.png\\\",\\\"\\/uploads\\/20190123\\/naae\\/5003c91efc26f7d7de280b924a5f7fea.png\\\"]\",\"page\":\"15\",\"title_catalog\":\"反馈·Feedback\",\"fifteen_text\":\"\",\"fifteen_pic_top\":\"\\/uploads\\/20190123\\/naae\\/79a9f1ca31203b6279d5d7a6eb252ef9.png\",\"fifteen_pic_low\":\"\",\"content15\":\"<p style=\\\"text-indent: 2em;\\\">在读学生及家长整体评价为<span style=\\\"color: rgb(95, 73, 122);\\\"><strong>4.5，<\\/strong><\\/span>其中四星及以上评价占<span style=\\\"color: rgb(95, 73, 122);\\\"><strong>91%。<\\/strong><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"color: rgb(95, 73, 122);\\\"><strong>86%<\\/strong><\\/span>的学生认为学校的同学很友好、积极参与各种活动、学习刻苦。<\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"color: rgb(95, 73, 122);\\\"><strong>100%<\\/strong><\\/span>的在读学生认为他们学校非常的安全；<span style=\\\"color: rgb(95, 73, 122);\\\"><strong>80%<\\/strong><\\/span>的学生喜欢该学校并且觉得在学校很开心。<\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"color: rgb(95, 73, 122);\\\"><strong>100%<\\/strong><\\/span>的学生认为该校的学习氛围浓厚；<span style=\\\"color: rgb(95, 73, 122);\\\">84%<\\/span>的学生能够严肃对待学业。<\\/p>\"}', 15, 1548209182);
INSERT INTO `clt_naae_media` VALUES (216, 41, '学校费用·Tuition', '{\"id\":\"\",\"pics_16\":\"[\\\"\\/uploads\\/20190123\\/naae\\/36ed4271e69134a02135a021a9203d99.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/56d5e96c549230533c0f287a64237709.jpg\\\"]\",\"page\":\"16\",\"title_catalog\":\"学校费用·Tuition\",\"sixteen_two_level_title\":\"62300美金\\/年\",\"sixteen_summary\":\"该费用包括学费、食宿费（10个月）、国际生费。此报价为预估为费用，每年可能会有5%-10%的涨幅，具体以invoice为准。（学生需额外缴纳3000元的入学指导费，同时根据学生的语言情况，额外缴纳ESL语言费用及学习中心费用预计20000美金左右）。\"}', 16, 1548209248);
