INSERT INTO `clt_naae_media` VALUES (232, 53, '学校认证·School Accredit', '{\"id\":\"\",\"pics_4\":\"[\\\"\\/uploads\\/20190123\\/naae\\/0de84f3a24c503c937671b497f9606a8.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/261dfda0810afb9b9989809e62d70897.jpg\\\"]\",\"page\":\"4\",\"title_catalog\":\"学校认证·School Accredition\",\"authentication_one\":\"国家基督教学校协会(NCSA)\",\"authentication_two\":\"美国中部诸州学院及高校联盟(MSA)\",\"authentication_three\":\"亚特兰大中部基督教学校协会(MACSA)\",\"authentication_four\":\"国际基督教学校协会(ACSI)\",\"authentication_five\":\"古典基督教学校协会(ACCS)\",\"authentication_six\":\"国家基督教学校协会(NCSA)\",\"four_top_pic\":\"\\/uploads\\/20190123\\/naae\\/20c7bb59f46a5288378ef98c8b8f4cbf.jpg\",\"four_right_pic\":\"\\/uploads\\/20190123\\/naae\\/047f6c6e66d51d017804a9a783e03b15.jpg\"}', 4, 1548214664);
INSERT INTO `clt_naae_media` VALUES (233, 53, '推荐理由·Recommended Rea', '{\"id\":\"\",\"pics_5\":\"[\\\"\\/uploads\\/20190123\\/naae\\/62814906f968a9d887619342266a9d43.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/53eaa09bcf97d23afd25137390312287.jpg\\\"]\",\"page\":\"5\",\"title_catalog\":\"推荐理由·Recommended Reasons\",\"five_text\":\"\",\"content5\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">1.<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>学校距离费城仅仅45分钟车程，<\\/strong><\\/span>学生可以在上课之余的空闲时间前往费城，参观博物馆，去宾夕法尼亚大学，布林茅尔学院感受名校气息。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">2.学校学术水平高，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">2016年有两个学生SAT获得满分。<\\/span><\\/strong><\\/span>学校升学情况好。<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>100%的升学率，<\\/strong><\\/span>历年毕业院校包括耶鲁大学等美国排名前三十的知名高校。<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>师生比例1：10，<\\/strong><\\/span>真正做到小班式教学，学生在学习和生活上得到极大的关注.学校师资力量雄厚，教学质量高。<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>75%以上老师拥有硕士及以上学位。学校为学生安排1对1升学指导老师。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">3.学校非常<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>注重团队协作意识，<\\/strong><\\/span>一进校门有五栋教学楼，学生分为五队，比赛，活动，学术都是以团队为基础，注重学生的思维发展。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">4.学校有<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>STEM项目，<\\/strong><\\/span>特别是<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>3D技术特别强。<\\/strong><\\/span>走在科技教育的前段，选修课设置<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(31, 73, 125);\\\"><strong>计算机，科学发现，3D打印<\\/strong><\\/span>等课程。<\\/span><\\/p><p><br\\/><\\/p>\"}', 5, 1548214823);
INSERT INTO `clt_naae_media` VALUES (234, 53, '学术特色·Academic Featur', '{\"id\":\"234\",\"pics_6\":\"[\\\"\\/uploads\\/20190123\\/naae\\/cdfb136b1f75d969c3a7d2be7ddc6ba5.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/9df29f6cafb254d261db01aa2d4049be.jpg\\\"]\",\"page\":\"6\",\"title_catalog\":\"学术特色·Academic Features\",\"six_text\":\"\",\"content6\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">Niche调查中，学校学术评级大学升学预备均是<span style=\\\"font-family: 宋体, SimSun; color: rgb(31, 73, 125);\\\"><strong>A级别，学校SAT均分1250，<\\/strong><\\/span>远高于美国平均水平（全美SAT均分1080，ACT均分23）。<\\/span><\\/p>\"}', 6, 1548214963);
INSERT INTO `clt_naae_media` VALUES (235, 53, '课程设置·Curriculum', '{\"id\":\"\",\"page\":\"7\",\"title_catalog\":\"课程设置·Curriculum\",\"seven_pic\":\"\\/uploads\\/20190123\\/naae\\/8574208446dd1686af2bef7833628a05.jpg\",\"z_d_y_data\":\"{\\\"人类学\\\":\\\"古代世界文学~古代世界历史~中世纪历史~中世纪文学~现代世界文学~现代世界历史\\\",\\\"逻辑与修饰\\\":\\\"逻辑错误概述~正式文学概述~修饰~毕业课题\\\",\\\"语言\\\":\\\"西班牙语1，2，3~拉丁语6，7，8~英语\\\",\\\"数学\\\":\\\"几何预备~代数1~几何~代数2~微积分预备~大学几何\\\"}\"}', 7, 1548215160);
INSERT INTO `clt_naae_media` VALUES (236, 53, '课程设置·Curriculum', '{\"id\":\"\",\"page\":\"8\",\"title_catalog\":\"课程设置·Curriculum\",\"eight_pic\":\"\\/uploads\\/20190123\\/naae\\/c981a8e33b53e1bc0ea4fa265a03aa7a.jpg\",\"z_d_y_data\":\"{\\\"科学\\\":\\\"地球科学~物理~生物~化学概述~物体科学~进阶生物~解剖学\\\",\\\"圣经\\\":\\\"圣经7，8~圣经神学~世界观分析~系统神学~宗教哲学\\\",\\\"选修\\\":\\\"辩论~科学发现~SAT数学~年鉴~健身科学~戏剧~3D打印设计~计算机概述~工作室艺术~舞台设计~合唱~学业准备\\\",\\\"AP课程\\\":\\\"微积分A\\\\\\/B~化学~生物~微观经济学~美国政府~英语文学~艺术史~美国史\\\"}\"}', 8, 1548216023);
INSERT INTO `clt_naae_media` VALUES (237, 53, '体育特色·Sports Activiti', '{\"pics_9_top\":\"[\\\"\\/uploads\\/20190123\\/naae\\/ef85a55af5e3d4fd540b3ffe774f57fd.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/429fd4ef410e573a961e06dd39853684.jpg\\\"]\",\"pics_9_low\":\"[\\\"\\/uploads\\/20190123\\/naae\\/5ac848d878c4d61c5f989ee034bfda35.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/2cf37e0230ae484ae9572143fa58d25d.jpg\\\"]\",\"id\":\"\",\"pics_9\":\"\",\"page\":\"9\",\"title_catalog\":\"体育特色·Sports Activities\",\"z_d_y_data\":\"{\\\"体育特色\\\":\\\"考文垂基督学校是大西洋海岸基督运动委员会成员，学生可以参加各项体育运动。\\\",\\\"男子\\\":\\\"足球，篮球，越野，田径，棒球\\\",\\\"女子\\\":\\\"排球，篮球，足球，越野，田径\\\"}\"}', 9, 1548216153);
INSERT INTO `clt_naae_media` VALUES (238, 53, '艺术特色·Fine Arts', '{\"id\":\"\",\"pics_10\":\"[\\\"\\/uploads\\/20190123\\/naae\\/5b18516329e726cfb6babe190165dc10.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/3ef41649d45661f4f7a3d6bd14fd958a.jpg\\\"]\",\"page\":\"10\",\"title_catalog\":\"艺术特色·Fine Arts\",\"ten_pic\":\"\\/uploads\\/20190123\\/naae\\/f6a6e4fd43ceda8de29a4a43815291c8.jpg\",\"z_d_y_data\":\"{\\\"艺术特色\\\":\\\"我们还鼓励学生积极参与表演，在我们的保障和支持下进行表演，从而享受乐趣自尊自信，鼓励团队合作，提升领导能力，弘扬学校精神。学校提供表演、视觉艺术、合唱、乐队等艺术领域的培养。\\\"}\"}', 10, 1548216231);
INSERT INTO `clt_naae_media` VALUES (239, 53, '学生社团·Club Activities', '{\"id\":\"\",\"pics_11\":\"[\\\"\\/uploads\\/20190123\\/naae\\/e21bdfbe16251ed29e5432b147c240a5.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/f829d87a09b8bf23dedb84ac543e32ad.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/20ac23523147757004a6a565b5634048.jpg\\\"]\",\"page\":\"11\",\"title_catalog\":\"学生社团·Club Activities\",\"eleven_summary\":\"社团活动丰富多彩，学生和家长满意度居高不下，好评如潮。\\r\\n戏剧演出、合唱团、乐队、背包旅行、校际体育、青年与政府、科学展览、啦啦队、力量训练、新闻社、年鉴、科学研究、历史研究、象棋俱乐部、工程俱乐部、创意写作、艺术工作室、健康和健身扩展、计算机科学，文学批判、电脑编程。\"}', 11, 1548216305);
INSERT INTO `clt_naae_media` VALUES (240, 53, '学校设施·Facilities', '{\"id\":\"\",\"pics_12\":\"[\\\"\\/uploads\\/20190123\\/naae\\/a26c9739cdfb9b2782fa2da2e14eef2f.png\\\",\\\"\\/uploads\\/20190123\\/naae\\/84ed23fc4563634a35818199c511fc23.png\\\",\\\"\\/uploads\\/20190123\\/naae\\/30845929a855d5e56a3fb734d5cf3cf2.png\\\"]\",\"page\":\"12\",\"title_catalog\":\"学校设施·Facilities\",\"z_d_y_data\":\"{\\\"室外\\\":\\\"篮球场，足球场，排球场，图书馆\\\",\\\"室内\\\":\\\"艺术教室，健身房，食堂，礼拜堂，学校商店，自助餐厅，高中科学实验室\\\"}\"}', 12, 1548216396);
INSERT INTO `clt_naae_media` VALUES (241, 53, '大学升学情况·University-En', '{\"id\":\"241\",\"pics_13\":\"\",\"page\":\"13\",\"title_catalog\":\"大学升学情况·University-Entrance Status\",\"thirteen_summary\":\"100%的升学率，98%进入四年制大学。该校的学生申请最多的大学有如下：\",\"thirteen_pic_top\":\"\\/uploads\\/20190123\\/naae\\/47463faa888c0a42ac5d57fead99a558.png\",\"thirteen_pic_low\":\"\\/uploads\\/20190123\\/naae\\/f44925f24d6934114ec775c9f83fdd71.jpg\"}', 13, 1548221752);
INSERT INTO `clt_naae_media` VALUES (242, 53, '社区概况·Community Profi', '{\"id\":\"242\",\"pics_14\":\"[\\\"\\/uploads\\/20190123\\/naae\\/b5324c24e78db32c43cf29536a71f27f.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/3f7fda82cb14352ea95f10424088e67e.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/c6b9753535a81d44e28e5044dadf8699.png\\\",\\\"\\/uploads\\/20190123\\/naae\\/5a209d3ac11c71dc5801d623b3c1e02d.png\\\"]\",\"page\":\"14\",\"title_catalog\":\"社区概况·Community Profile\",\"fourteen_text\":\"\",\"content14\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校距离费城仅仅45分钟车程，距离费城国际机场45分钟，学生可以在上课之余的空闲时间前往费城，参观博物馆，去宾夕法尼亚大学，布林茅尔学院感受名校气息。考文垂基督学校所在地区 萨纳托加 是宾夕法尼亚州的一个城镇，隶属于费城，居民多持有温和的政治观点。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该地区在全美最适合居住城市中位于中上水平。在城市多元化以及宜居程度等方面，均高于宾夕法尼亚州的平均水平。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该地区为富人区，当地的家庭人均财产为$63,510，高于全美平均水平。居民受教育水平略低于全国平均水平。<\\/span><\\/p>\"}', 14, 1548222015);
INSERT INTO `clt_naae_media` VALUES (243, 53, '反馈·Feedback', '{\"id\":\"\",\"pics_15\":\"[\\\"\\/uploads\\/20190123\\/naae\\/19f22904e1f5fdf3da238c33c486e6e4.png\\\",\\\"\\/uploads\\/20190123\\/naae\\/60b121a154e401958dffe6f676d151c5.png\\\"]\",\"page\":\"15\",\"title_catalog\":\"反馈·Feedback\",\"fifteen_text\":\"\",\"fifteen_pic_top\":\"\\/uploads\\/20190123\\/naae\\/0bea161bc287e8f0815b15b387543d14.png\",\"fifteen_pic_low\":\"\\/uploads\\/20190123\\/naae\\/b7f932342e5189d3564fde521ca28840.jpg\",\"content15\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">在读学生及家长整体评价为<span style=\\\"font-family: 宋体, SimSun; color: rgb(31, 73, 125);\\\"><strong>4.8，<\\/strong><\\/span>其中<span style=\\\"font-family: 宋体, SimSun; color: rgb(31, 73, 125);\\\"><strong>五星评价占57%，<\\/strong><strong>四星评价占26%。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-indent: 2em;\\\">Niche调查显示，学生们最喜欢的活动分别为返校节，社区服务周，校园精神周，球类运动，校园戏剧，各占20%。<\\/span><\\/p>\"}', 15, 1548222493);
INSERT INTO `clt_naae_media` VALUES (244, 53, '学校费用·Tuition', '{\"id\":\"\",\"pics_16\":\"[\\\"\\/uploads\\/20190123\\/naae\\/9cb527ad5dc22dd3cb3d298dfeb7dabe.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/5c4c1a52e73656b9299f673679a26d1a.jpg\\\"]\",\"page\":\"16\",\"title_catalog\":\"学校费用·Tuition\",\"sixteen_two_level_title\":\"46975美金\\/年\",\"sixteen_summary\":\"该费用包括学费、食宿费、餐费、国际生管理费。此报价为预估为费用，每年可能会有5%-10%的涨幅，具体以invoice为准。(该费用不包含ESL语言辅导费用，需要根据学生自身语言情况额外缴纳）\"}', 16, 1548222557);
