INSERT INTO `clt_naae_media` VALUES (249, 54, '学校认证·School Accredit', '{\"id\":\"249\",\"pics_4\":\"[\\\"\\/uploads\\/20190123\\/naae\\/264106d13a8e56a4b64b2d124e413aaa.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/734ae4c734b11fef4e0e76c9f5370519.jpg\\\"]\",\"page\":\"4\",\"title_catalog\":\"学校认证·School Accreditation\",\"authentication_one\":\"北部中心协会认证\",\"authentication_two\":\"门诺教育中介认证\",\"authentication_three\":\"美国基督教学校认证 \",\"authentication_four\":\"美国东北部学校认证\",\"authentication_five\":\"北部中心协会认证\",\"authentication_six\":\"门诺教育中介认证\",\"four_top_pic\":\"\\/uploads\\/20190123\\/naae\\/77aec68ae218894d638121516800a40f.jpg\",\"four_right_pic\":\"\\/uploads\\/20190123\\/naae\\/0a6603350abe354beaf8a846d33c7057.jpg\"}', 4, 1548227712);
INSERT INTO `clt_naae_media` VALUES (250, 54, '推荐理由·Recommended Rea', '{\"id\":\"\",\"pics_5\":\"[\\\"\\/uploads\\/20190123\\/naae\\/30ea521407c4b40a34c81cb5cb7e1e87.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/03def298437c64f107994ecdd4b6c320.jpg\\\"]\",\"page\":\"5\",\"title_catalog\":\"推荐理由·Recommended Reasons\",\"five_text\":\"\",\"content5\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">多可门诺学院拥有近70年的建校史，在全美最好的基督教高中排名前<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>18%，<\\/strong><\\/span>是宾夕法尼亚州最好的基督教高中<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>第15名，<\\/strong><\\/span>是美国权威高中排名Niche排名中的<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>A+类院校。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校教学资源丰富，拥有一支优秀的师资团队，其中拥有研究生及以上学历的老师<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>超75%；<\\/strong><\\/span>同时，学校开设了种类丰富、规划科学的课程，不管是<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>文化课，<\\/strong><\\/span>还是<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>艺术体育、社会生活<\\/strong><\\/span>方面，都为学生营造了一个积极、良好的学习氛围，如学校开设有室内驾驶课、烹饪学、房屋和设计等课，16门的AP课程等。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">在<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>体育与艺术<\\/strong><\\/span>的培养上，学校坚持学习与实践并重的方式，让学生不仅能在学校设置的各类课程中充分汲取体育与艺术方面的知识和技巧，还能够在学校举办的各类活动中充分展示自己、发挥自己。如在学校的培养和锻炼下，学生Austin Kratz与他的团队在芬兰坦佩雷U20国际田径联合会世锦赛上获得了金奖，此外Austin Kratz还是宾夕法尼亚州60米和200米短跑的记录保持者。在艺术方面，学校每年二月份还会庆祝热闹的<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>“艺术日”<\\/strong><\\/span>，通过设置一个特定的主题，让同学们开动智慧，用各种方式探索艺术、享受艺术。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学生可以充分培养在<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>AI人工智能<\\/strong><\\/span>方面的兴趣。校内有<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>机器人俱乐部，<\\/strong><\\/span>高新科技部，学生可以近距离的接触最热门的科技前沿和科学应用。<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>机器人项目<\\/strong><\\/span>全美闻名，机器人小组在全美高中名列前茅，在历届机器人大赛中均有获奖。<\\/span><\\/p><p><br\\/><\\/p>\"}', 5, 1548227970);
INSERT INTO `clt_naae_media` VALUES (251, 54, '学术特色·Academic Featur', '{\"id\":\"\",\"pics_6\":\"[\\\"\\/uploads\\/20190123\\/naae\\/3591bacdc38fc761cd6da9ecc8efa62d.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/b89e8484e4c64898264d1229a8508353.jpg\\\"]\",\"page\":\"6\",\"title_catalog\":\"学术特色·Academic Features\",\"six_text\":\"\",\"content6\":\"<p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">Niche调查中，学校学术评级为<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>A级别，学校SAT均分1210，ACT29分，<\\/strong><\\/span>较高于美国平均水平（全美SAT均分1100，ACT均分21）。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校师生比例很低，为<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>1:12，<\\/strong><\\/span>同时该校<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>75%<\\/strong><\\/span>的老师都拥有高级学历,这些保证了学生能够受到老师保质保量的关注和教育。从Niche的调查结果来看，79%的学生和家长表示老师能充分地照顾和关心到同学们,83%的学生和家长感觉老师能够很好地管理课堂。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-align: justify; text-indent: 2em;\\\">学校的课程开设很全面，除了注重学校学术的教学，开设了<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-align: justify; text-indent: 2em; color: rgb(118, 146, 60);\\\"><strong>16门AP课程，<\\/strong><\\/span>同时学校还开设了大量的艺术课程、职业课程、计算机实践课程等。学校的机器人和人工智能课程实力很强，多次获得机器人大赛奖项，有学生凭借机器人奖项，申请到美国麻省理工大学。<\\/span><\\/p>\"}', 6, 1548228119);
INSERT INTO `clt_naae_media` VALUES (252, 54, '课程设置·Curriculum', '{\"id\":\"\",\"page\":\"7\",\"title_catalog\":\"课程设置·Curriculum\",\"seven_pic\":\"\\/uploads\\/20190123\\/naae\\/ff3d5f166c7108afe9e9167dc48e9aa0.jpg\",\"z_d_y_data\":\"{\\\"基础课程\\\":\\\"代数~几何学~统计学~高等数学~微积分学~生物学~化学~环境科学~物理学~世界文化~美国历史~社会问题学~美国政府~经济学~全球史~宏观经济学~办公软件应用~网站设计~会计学基础~编程学~机器人学~网站设计~商学导论~计算机动画~视频制作~西班牙语~美国文学\\\",\\\"艺术课程\\\":\\\"平面设计~徒手绘画~制陶术~雕塑~电影研究~摄影~水彩~独立工作室~艺术史~绘画~作品集制作\\\"}\"}', 7, 1548228353);
INSERT INTO `clt_naae_media` VALUES (253, 54, '课程设置·Curriculum', '{\"id\":\"253\",\"page\":\"8\",\"title_catalog\":\"课程设置·Curriculum\",\"eight_pic\":\"\\/uploads\\/20190123\\/naae\\/0a5f0c097390332fd6ea3dc9afd54b86.jpg\",\"z_d_y_data\":\"{\\\"音乐课程\\\":\\\"基础音乐~合唱团~古典吉他~乐器即兴表演~管弦乐~音乐会乐队~古典钢琴\\\",\\\"AP课程\\\":\\\"文科~理科~统计学1~统计学2~微积分AB~微积分BC~生物学~物理学A~环境科学~美国政府~宏观经济学~西班牙语4-语言和文化~西班牙语-文学~艺术史~英语文学和写作~微观经济学~心理学~世界史\\\"}\"}', 8, 1548228697);
INSERT INTO `clt_naae_media` VALUES (254, 54, '体育特色·Sports Activiti', '{\"pics_9_top\":\"[\\\"\\/uploads\\/20190123\\/naae\\/08cc70a75f96f6e2b1f53f6e47f3e746.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/a808b71852109f8f19deef2898d7361d.jpg\\\"]\",\"pics_9_low\":\"[\\\"\\/uploads\\/20190123\\/naae\\/0aaebc7f69b9cecd0ea0cb48050e8698.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/401889cdbb1cca898ca2ab5d17efb64f.jpg\\\"]\",\"id\":\"\",\"pics_9\":\"\",\"page\":\"9\",\"title_catalog\":\"体育特色·Sports Activities\",\"z_d_y_data\":\"{\\\"体育特色\\\":\\\"篮球、足球、排球、棒球、羽毛球、田径、野外越野、曲棍球、保龄球和高尔夫球等。学校在田径方面的培养上取得了引人注目的成绩，如2018年该校的学生AustinKratz与他的团队在芬兰坦佩雷U20国际田径联合会世锦赛上获得了金奖，此外AustinKratz还是宾夕法尼亚州60米和200米短跑的记录保持者，在全美60米和200米短跑排名中AustinKratz分别位列第四和第三名。2005年、2010年棒球队均获得州冠军，2009-2013年足球队连续四次获得州冠军；2015-2016年田径赛，学校共获得了14项总冠军，并打破了历史记录。\\\"}\"}', 9, 1548229593);
INSERT INTO `clt_naae_media` VALUES (255, 54, '艺术特色·Fine Arts', '{\"id\":\"\",\"pics_10\":\"[\\\"\\/uploads\\/20190123\\/naae\\/979ff12f9746676e5fad91951cec4678.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/32e5392a0f9bf2b15f52c45dbce74156.jpg\\\"]\",\"page\":\"10\",\"title_catalog\":\"艺术特色·Fine Arts\",\"ten_pic\":\"\\/uploads\\/20190123\\/naae\\/3f24e9563bbf48d747dc44c318d699e2.jpg\",\"z_d_y_data\":\"{\\\"艺术特色\\\":\\\"多可门诺学院十分注重学生艺术方面的培养和发展，因此不管在课程设置，还是相关硬件设施的配备上，都做出了巨大努力。在多可门诺，学生可学习各类音乐舞蹈方面的课程，如古典钢琴，古典吉他、管弦乐、多种形式的合唱等；同时还能参与到各类艺术课程的探索中，如平面设计、制陶术、雕塑、电脑动画、水彩等。学校为了让学生在学习之后，还能有充分展示自己、证明自己的机会，在每年二月还专门设置了“艺术日”，以给学生们一个享受艺术、创作艺术的绝佳机会。\\\"}\"}', 10, 1548229730);
INSERT INTO `clt_naae_media` VALUES (256, 54, '学生社团·Club Activities', '{\"id\":\"\",\"pics_11\":\"[\\\"\\/uploads\\/20190123\\/naae\\/47bffc41ebda06346a2617327ba10d6f.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/01afd6baa9e2a48808b659cb0f7dd025.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/820cf37d564acddb3f104ca573793ffc.jpg\\\"]\",\"page\":\"11\",\"title_catalog\":\"学生社团·Club Activities\",\"eleven_summary\":\"学校除了在文化课上对学生进行了精心地培养，还通过编排设置各类有趣且有富有意义的社团和课外活动对学生的个人品质和动手能力进行锻炼提升。如学校设有国家荣誉社团，精神俱乐部，德语俱乐部,法语俱乐部，西班牙语俱乐部，国际俱乐部，当代美国思想，圣经学习,游戏视频俱乐部，大学视觉俱乐部，机器人俱乐部，电影俱乐部等。\"}', 11, 1548229818);
INSERT INTO `clt_naae_media` VALUES (257, 54, '学校设施·Facilities', '{\"id\":\"257\",\"pics_12\":\"[\\\"\\/uploads\\/20190123\\/naae\\/0e065657a7bfbc47937e75076b094534.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/1492fba7aca5892a9aa6e8f6a565fb05.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/40a90808ac4908effe6a0e1d562156e7.jpg\\\"]\",\"page\":\"12\",\"title_catalog\":\"学校设施·Facilities\",\"z_d_y_data\":\"{\\\"学校设施\\\":\\\"学校分别为中小学和高中部设立了不同的校区，并且在两个校区都有完备的教学设施、建筑，以及艺术、体育相关的器械和场馆等。如学校有六栋教学楼，在中小学部和高中部分别都设有图书馆、满足各类户外运动的操场、自助餐厅、健身房等。同时学校的大礼堂、宴会厅，可举行数百人的学术交流、文化活动等。\\\"}\"}', 12, 1548229976);
INSERT INTO `clt_naae_media` VALUES (258, 54, '大学升学情况·University-En', '{\"id\":\"\",\"pics_13\":\"\",\"page\":\"13\",\"title_catalog\":\"大学升学情况·University-Entrance Status\",\"thirteen_summary\":\"很多学生进入了美国受欢迎的大学和艺术院校，以及一些著名的国际院校；该校的学生申请较多的大学有如下：\",\"thirteen_pic_top\":\"\\/uploads\\/20190123\\/naae\\/fdabdab23d23bacca08c24cc36289df6.png\",\"thirteen_pic_low\":\"\\/uploads\\/20190123\\/naae\\/4ed930c772a24ac270ca165d7c470575.jpg\"}', 13, 1548230173);
INSERT INTO `clt_naae_media` VALUES (259, 54, '社区概况·Community Profi', '{\"id\":\"259\",\"pics_14\":\"[\\\"\\/uploads\\/20190123\\/naae\\/485187ce547fb428a7b39aaf7bd0f4a2.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/b48694b783797227cf20208b2f73db68.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/8e1d648c15c3fcb6325731ea31ddecbb.png\\\"]\",\"page\":\"14\",\"title_catalog\":\"社区概况·Community Profile\",\"fourteen_text\":\"\",\"content14\":\"<p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">多可门诺学院位于美国宾夕法尼亚州蒙哥马利县的卡尔普斯维尔,该地区是蒙哥马利县人口普查指定地点，同时也是宾夕法尼亚州最适宜人们生活和居住的地方之一。作为宾夕法尼亚州的一个市郊，这里有很多的公园，很适宜人们进行户外活动，因而有很多人也把该地区作为退休后养老的选择。2010年人口普查该地区人口数为8194人，其中<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>83%为白人，<\\/strong><\\/span>8%为亚裔人种。在交通方面，驾车到费城约需<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>34分钟，<\\/strong><\\/span>到新泽西州首府特伦顿约需<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>46分钟；<\\/strong><\\/span>如果需要搭乘飞机，驾车到费城国际机场约需35分钟。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该地区在全美拥有最好高中的市郊排名中位列前4%，在全美最适合居家生活排名中位列前17%。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该地区人口相对较少，总人口数为8859；男女比例相对均衡，基本达到1:1；该地区大部分为白人，占到了77%，其中有10%为亚裔人种。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该地区居民受教育程度很高,获得本科及以上学历的居民占到<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>73%。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该地区的房产价值相对较高，居民的家庭收入高出全国水平10000多美元，居民的房屋拥有率超70%。<\\/span><\\/p>\"}', 14, 1548230396);
INSERT INTO `clt_naae_media` VALUES (260, 54, '反馈·Feedback', '{\"id\":\"260\",\"pics_15\":\"[\\\"\\/uploads\\/20190123\\/naae\\/3356fc227a2a82b219a1ccf7da29b2ec.png\\\",\\\"\\/uploads\\/20190123\\/naae\\/ccd9c15e4ecf68602324f2b7580fc86e.png\\\",\\\"\\/uploads\\/20190123\\/naae\\/7bef6d855ef3aabc1f9ffd5f04d2ef66.png\\\",\\\"\\/uploads\\/20190123\\/naae\\/793919c47cc1124d47d1c362f9965c62.png\\\"]\",\"page\":\"15\",\"title_catalog\":\"反馈·Feedback\",\"fifteen_text\":\"\",\"fifteen_pic_top\":\"\\/uploads\\/20190123\\/naae\\/6e02e8e5dfa3915b88afe0e8e828b1fe.png\",\"fifteen_pic_low\":\"\",\"content15\":\"<p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学生和家长对学校的整体评价为<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>四星半，<\\/strong><\\/span>其中五星级评价占到<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>52%，<\\/strong><\\/span>四星级评价占到<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>32%，<\\/strong><\\/span>四星级及以上评价总计占到<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(118, 146, 60);\\\"><strong>84%。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校共有845名学生，男女比例近似1:1，白人占比超76%，亚裔学生占11.8%；其中86%的学生表示学校的同学很友好，86%的学生与同学们参与了学校活动，86%的学生感觉同学们学习都很刻苦。<\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">80%的在读学生感觉他们在学校很安全；80%的在读学生表示他们很喜欢这所学校，他们在那里很快乐。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-align: justify; text-indent: 2em;\\\">Niche调查结果显示，该校在社团和课外活动的安排上评级达到A，同时男生、女生都高度参与到了学校组织的体育活动中。其中，73%的学生和家长表示学校设置了很多同学们能够参与其中的社团组织。<\\/span><\\/p>\"}', 15, 1548231266);
INSERT INTO `clt_naae_media` VALUES (261, 54, '学校费用·Tuition', '{\"id\":\"\",\"pics_16\":\"[\\\"\\/uploads\\/20190123\\/naae\\/b4d616586b494e4b0fb11139c576f684.jpg\\\",\\\"\\/uploads\\/20190123\\/naae\\/5d19b234fce16f548828d6c35fa4f1ae.jpg\\\"]\",\"page\":\"16\",\"title_catalog\":\"学校费用·Tuition\",\"sixteen_two_level_title\":\"58500美金\\/年\",\"sixteen_summary\":\"该费用包括学费、寄宿费、ESL费用、餐费、管理费、保险费。此报价为预估费用，每年可能会有5%-10%的涨幅，具体以invoice为准。\"}', 16, 1548231374);
