INSERT INTO `clt_naae_media` VALUES (263, 55, '罗斯中学', '{\"id\":\"\",\"page\":\"1\",\"title_catalog\":\"罗斯中学\",\"two_level_title\":\"Ross School\",\"form_summary\":\"纽约州私立高中前10的顶级蓝带贵族学校；87%的老师具备硕士以上文凭；约10%左右的毕业生都可进入普林斯顿，耶鲁，哈佛等名校;学校设施非常华丽，宿舍温馨，配置齐全。\",\"one_pic\":\"\\/uploads\\/20190123\\/naae\\/5880c450f6521ccf1f375b5ad07befac.jpg\"}', 1, 1548238812);
INSERT INTO `clt_naae_media` VALUES (264, 55, '学校概况·Overview', '{\"id\":\"264\",\"page\":\"2\",\"title_catalog\":\"学校概况·Overview\",\"two_pic\":\"\\/uploads\\/20190124\\/naae\\/45c74c3ed052ff04aa217b7682c36fcb.png\",\"z_d_y_data\":\"{\\\"建校时间\\\":\\\"1991年\\\",\\\"学生人数\\\":\\\"368人\\\",\\\"占地面积\\\":\\\"30英亩\\\",\\\"宗教背景\\\":\\\"无\\\",\\\"周边城市\\\":\\\"纽约\\\",\\\"学校性质\\\":\\\"男女合校\\\",\\\"年级设置\\\":\\\"K-12年级\\\",\\\"招生年级\\\":\\\"7-12年级\\\",\\\"住宿类型\\\":\\\"寄宿\\\",\\\"师生比例\\\":\\\"1:5\\\",\\\"国际学生\\\":\\\"202%\\\",\\\"课堂规模\\\":\\\"7人\\\",\\\"高级教师\\\":\\\"75%以上硕士学位\\\",\\\"SAT均分\\\":\\\"1320\\\",\\\"AP课程\\\":\\\"14门\\\",\\\"入学要求\\\":\\\"TOEFL\\\",\\\"学校官网\\\":\\\"www.ross.org\\\",\\\"申请费\\\":\\\"100美金\\\"}\"}', 2, 1548239796);
INSERT INTO `clt_naae_media` VALUES (265, 55, '学校排名·School Ranking', '{\"id\":\"\",\"pics_3\":\"[\\\"\\/uploads\\/20190124\\/naae\\/4eda9d2f1a3cd8f204d6a2642eb9fe02.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/e1a222dfd7c2181f435fba063908d712.png\\\"]\",\"page\":\"3\",\"title_catalog\":\"学校排名·School Ranking\",\"ranking_one\":\"美国权威高中排名，Niche排名A+类院校\",\"ranking_two\":\"全美私立高中排名前25%\",\"ranking_three\":\"纽约私立高中排名前35%\",\"three_top_pic\":\"\\/uploads\\/20190124\\/naae\\/b025c126848033debcec0b92ba409778.png\",\"three_low_pic\":\"\\/uploads\\/20190124\\/naae\\/a962afbb81ba24a164406e2f4df9c606.jpg\"}', 3, 1548293856);
INSERT INTO `clt_naae_media` VALUES (266, 55, '学校认证·School Accredit', '{\"id\":\"\",\"pics_4\":\"[\\\"\\/uploads\\/20190124\\/naae\\/90b60b0f906fa622810d60edfc7b3e78.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/aabc04cf824b91c5a37f0a1acf5b60e5.jpg\\\"]\",\"page\":\"4\",\"title_catalog\":\"学校认证·School Accreditation\",\"authentication_one\":\"纽约州独立学校协会认证\",\"authentication_two\":\"中美州中小学校委员会首个国际认证\",\"authentication_three\":\"全球文学界的杰出计划\",\"authentication_four\":\"多次被评为“蓝带”中学\",\"authentication_five\":\"美国中部大学和中学协会认证\",\"authentication_six\":\"美国大学入学考试委员会和大学奖学金服务机构成员\",\"four_top_pic\":\"\\/uploads\\/20190124\\/naae\\/a7abbc83e7f1e34151ba23e2b80bf4de.jpg\",\"four_right_pic\":\"\\/uploads\\/20190124\\/naae\\/96a7acabe914e55f3a1e21687a82874b.jpg\"}', 4, 1548294015);
INSERT INTO `clt_naae_media` VALUES (267, 55, '推荐理由·Recommended Rea', '{\"id\":\"\",\"pics_5\":\"[\\\"\\/uploads\\/20190124\\/naae\\/8f2002ab24084354c2e71a05e9623d27.jpg\\\"]\",\"page\":\"5\",\"title_catalog\":\"推荐理由·Recommended Reasons\",\"five_text\":\"\",\"content5\":\"<p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校地理位置优越，位于美国<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>纽约市长岛地区，距离纽约城只有两个小时，<\\/strong><\\/span>10分钟车程到美丽的海滩。学校风景优美，周边著名大学林立，教育氛围浓厚。且学校并不像纽约市中心那样喧闹繁华，保留着美好的自然景观和人文气息，极适宜学习生活。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校多次被评为全美<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>蓝带中学，在纽约州私立学校排名10，SAT均分1950分。<\\/strong><\\/span>和普通学校相比，蓝带学校教学理念先进科学，超前教育普遍，学习风气浓厚。蓝带学校老师的水平非常高。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">罗斯学校教育质量极高，拥有100%的升学率，且<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>绝大部分毕业生前往美国Top100顶级大学，<\\/strong><\\/span>学校每年进入常春藤名校的成功率也在全美高中名列前茅，大约10%左右的学生都可进入普林斯顿，耶鲁，哈佛等名校。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">罗斯学校致力于确保学生能够实现他们的学术和职业目标。项目开始于9年级之前的春季，辅导员与学生及其家长合作，为高中生概述全面的学术计划。每年秋季，来自全国各地的大学和学院的代表都会访问校园，并定期<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>向学生和家长提供大学和大学招生研讨会，以便在这个关键时期为他们提供支持。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校师资力量强大，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>87%的老师具备硕士及以上文凭，<\\/strong><\\/span>教师通过合作学习和个人发展促进学术卓越，提升学生的社会技能、自尊和对他人的尊重。同时<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>学校邀请一些社会著名学者和行业专家进行指导，协助课程设计和发展，指导教与学的行动研究，并参与教师专业发展研讨会。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">罗斯中学的<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>住宿条件非常出色。<\\/strong><\\/span>学校宿舍十分温馨，极力为学生营造了家的感觉，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>每个宿管老师只负责5名学生。<\\/strong><\\/span>屋外配有有游泳池、烧烤炉，房内有厨房、电视等，所有家具家电一应俱全。<\\/span><\\/p><p><br\\/><\\/p>\"}', 5, 1548294307);
INSERT INTO `clt_naae_media` VALUES (268, 55, '学术特色·Academic Featur', '{\"id\":\"268\",\"pics_6\":\"[\\\"\\/uploads\\/20190124\\/naae\\/b9a48ddf0d7abf1a85c22d32754b677d.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/d2f8bb2f5f7057de0cec2a26ea6f96c9.jpg\\\"]\",\"page\":\"6\",\"title_catalog\":\"学术特色·Academic Features\",\"six_text\":\"\",\"content6\":\"<p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">Niche调查中，学校学术评级大学升学预备均是<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>A+级别，“学术”项目的评级为A+；学校SAT均分1320，ACT30分，<\\/strong><\\/span>高于美国平均水平（全美SAT均分1080，ACT均分23）。<\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">小班制教学，学校<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>师生比例1:5，<\\/strong><\\/span>学生在学习和生活上得到极大的关注.学校师资力量雄厚，教学质量高。<\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">基于二十多年的教育研究和实践经验，罗斯学校创立了独特的<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>罗斯学习系统，提供八个综合学科的课程：文化史，世界语言和文学，科学，数学，视觉艺术，表演艺术，媒体研究与技术和健康。每个单元提供学习经验、评估、综合项目。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">所有学生都要求做一个<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>毕业设计，<\\/strong><\\/span>可以选择<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>科学、人文、艺术等各个领域的话题进行研究，<\\/strong><\\/span>学校会有专门的老师进行指导。同时学校还为学生提供到<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>国外实习<\\/strong><\\/span>的机会。<\\/span><\\/p>\"}', 6, 1548294502);
INSERT INTO `clt_naae_media` VALUES (269, 55, '课程设置·Curriculum', '{\"id\":\"\",\"page\":\"7\",\"title_catalog\":\"课程设置·Curriculum\",\"seven_pic\":\"\\/uploads\\/20190124\\/naae\\/3993d9ac130be1643ea676c9210cce10.jpg\",\"z_d_y_data\":\"{\\\"9年级\\\":\\\"文化历史（中世纪与现代）~综合英语9~综合物理及生物（实验室课程）~数学~外语（中文\\\\\\/西班牙语\\\\\\/法语）~综合艺术9~生活技能（生活能力\\\\\\/连通性和设计能力）~健康（可选校际运动课程）~两门自由选修课程\\\",\\\"10年级\\\":\\\"文化历史~综合英语10~综合化学~物理~地质学（实验室课程）~数学~外语（中文\\\\\\/西班牙语\\\\\\/法语）~综合艺术10~健康~两门可选课程\\\",\\\"11年级\\\":\\\"文化历史(全球化和现代化)~综合英语11~综合生物~物理~环境科学（实验室课程）~数学~外语（中文\\\\\\/西班牙语\\\\\\/法语）~综合艺术11~SAT\\\\\\/ ACT Prep~健康（可选校际运动课程）~两门可选课程\\\"}\"}', 7, 1548294949);
INSERT INTO `clt_naae_media` VALUES (270, 55, '课程设置·Curriculum', '{\"id\":\"270\",\"page\":\"8\",\"title_catalog\":\"课程设置·Curriculum\",\"eight_pic\":\"\\/uploads\\/20190124\\/naae\\/b901925132cd755d7d434e27a15cf264.png\",\"z_d_y_data\":\"{\\\"12年级\\\":\\\"文化历史~综合英语12~宇宙~生命~大脑和思想（实验室课程）~数学~外语(中文\\\\\\/西班牙语\\\\\\/法语)~高级项目\\\\\\/写作讲习~健康（可选校际运动课程）~三门可选课程\\\",\\\"选修课\\\":\\\"20世纪美国的大众文化~当代艺术~莎士比亚的喜剧~数字艺术~现代舞~电影史~Arduino简介~遗传学和伦理学~划船健身~社会企业家精神的原则~西非鼓手~芭蕾舞~风暴科学~机器人和基本编码~金属加工~比喻雕塑~空手道\\\",\\\"AP课程\\\":\\\"生物学~环境科学~化学~物理~微积分AB~微积分BC~统计学~微积分III（多变量）~文学~美国历史~世界历史~欧洲历史~雕塑~绘画\\\"}\"}', 8, 1548295318);
INSERT INTO `clt_naae_media` VALUES (271, 55, '体育特色·Sports Activiti', '{\"pics_9_top\":\"[\\\"\\/uploads\\/20190124\\/naae\\/92e35bcad628e6c9ebee2396ea080ba9.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/1a7b3d2770c1fadf7fa41f40c5b87025.png\\\"]\",\"pics_9_low\":\"[\\\"\\/uploads\\/20190124\\/naae\\/abbaf9750edc58f129a95146b28bcad0.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/d550f13ea3d2c85a5d8650b3ea875e9f.png\\\"]\",\"id\":\"\",\"pics_9\":\"\",\"page\":\"9\",\"title_catalog\":\"体育特色·Sports Activities\",\"z_d_y_data\":\"{\\\"秋季运动\\\":\\\"男子足球，女子足球，女子网球，女子排球，男生越野，女生越野，帆船俱乐部，冲浪俱乐部\\\",\\\"冬季运动\\\":\\\"男子篮球队，女子篮球，摔跤队\\\",\\\"春季运动\\\":\\\"男子网球，女子田径，男子田径，羽毛球俱乐部，男子曲棍球，女子曲棍球\\\"}\"}', 9, 1548295807);
INSERT INTO `clt_naae_media` VALUES (272, 55, '艺术特色·Fine Arts', '{\"id\":\"\",\"pics_10\":\"[\\\"\\/uploads\\/20190124\\/naae\\/8529aa75ccb44a258b4494d17c6876ad.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/92aba2a6f9a5ba875f66a7168c58b897.png\\\"]\",\"page\":\"10\",\"title_catalog\":\"艺术特色·Fine Arts\",\"ten_pic\":\"\\/uploads\\/20190124\\/naae\\/5e705872912d918f2db8d1c1da582784.png\",\"z_d_y_data\":\"{\\\"艺术特色\\\":\\\"我们还鼓励学生积极参与表演，在我们的保障和支持下进行表演，从而享受乐趣自尊自信，鼓励团队合作，提升领导能力，弘扬学校精神。艺术活动包括：低、中、高年级的合唱，Palio周年庆，春、秋季音乐剧，年度综艺演出、合唱团表演，体操和舞蹈的表演形式也会出现在学校各种演出当中。\\\"}\"}', 10, 1548295923);
INSERT INTO `clt_naae_media` VALUES (273, 55, '学生社团·Club Activities', '{\"id\":\"\",\"pics_11\":\"[\\\"\\/uploads\\/20190124\\/naae\\/cf13c82e98b3aaa70b3ee40e8bad9a0b.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/6b5123d97f97bf75fb3c865ea43ccaf0.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/c0658b0a93be7079388e5e536f11d856.jpg\\\"]\",\"page\":\"11\",\"title_catalog\":\"学生社团·Club Activities\",\"eleven_summary\":\"罗斯中学拥有的学生社团主要有：机器人俱乐部、篮球俱乐部、陶艺俱乐部、动画绘制俱乐部、马术俱乐部、时尚俱乐部、柔术协会、工艺品俱乐部、雕塑艺术俱乐部、年鉴等众多俱乐部和学生团体。\\r\\n学生可以在视觉艺术、表演艺术、媒体学等方面，获得老师专业的指导和培养。周末的活动丰富多彩。学校还组织武术，舞会，举重训练，瑜珈，山地自行车，帆船及许多其他课外活动。\"}', 11, 1548296143);
INSERT INTO `clt_naae_media` VALUES (274, 55, '学校设施·Facilities', '{\"id\":\"274\",\"pics_12\":\"[\\\"\\/uploads\\/20190124\\/naae\\/1f40f0a1ffc3fbd9b017c0f27bbb875c.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/d81535b94afe93c05791eed70dbfd57f.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/76fc37889b2041a918d7169424ae111a.png\\\"]\",\"page\":\"12\",\"title_catalog\":\"学校设施·Facilities\",\"z_d_y_data\":\"{\\\"\\\":\\\"罗斯高中为学生提供了丰富的技能和知识场所，从舞蹈工作室和表演场所到金属和木制商店，到充满艺术和文物的学术教室，运动场，科学实验室，以及各种各样的艺术，摄影和视听工作室。罗斯学生在课堂上的成长经历以及作为社区成员在罗斯的使命中教育整个孩子的整个世界 - 思想，精神和身体。 校内装潢极其精致，还融合了各国的文化元素，有非洲、印度、中国等等。而且随着年级的升高，教学楼装潢的风格变得越来越现代。每栋教学楼里都有图书馆，供学生读书学习。学校建有独立的健康中心，其中包括健身房、篮球场、舞蹈房等。健身房里还有普拉提房，思考室等。学校内的剧场可以改变成羽毛球场、篮球场等。而网球作为学校的优势项目，拥有独立练习区域。学校内有多个琴房、乐队训练室、乐器房。\\\"}\"}', 12, 1548296243);
INSERT INTO `clt_naae_media` VALUES (275, 55, '大学升学情况·University-En', '{\"id\":\"275\",\"pics_13\":\"\",\"page\":\"13\",\"title_catalog\":\"大学升学情况·University-Entrance Status\",\"thirteen_summary\":\"学校爬藤率很高，罗斯中学拥有100%的升学率，且绝大部分毕业生前往美国Top 100顶级大学。\",\"thirteen_pic_top\":\"\\/uploads\\/20190124\\/naae\\/a651425dde103cf06cc2c910bef632b7.png\",\"thirteen_pic_low\":\"\\/uploads\\/20190124\\/naae\\/633a513bc9f99d54dee6fc89017efad9.jpg\"}', 13, 1548296520);
INSERT INTO `clt_naae_media` VALUES (276, 55, '社区概况·Community Profi', '{\"id\":\"\",\"pics_14\":\"[\\\"\\/uploads\\/20190124\\/naae\\/53e0b430e611770ae68ed7196b8386f3.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/9e44af1a02078c7428e4b48ab1eae051.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/ec9b5fa5909378b24df3cf717b187ff7.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/ccabacce17ef3113b66e177d05322dad.png\\\"]\",\"page\":\"14\",\"title_catalog\":\"社区概况·Community Profile\",\"fourteen_text\":\"\",\"content14\":\"<p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">罗斯中学所在地区位于纽约州的萨福克郡，人口1,497,595。萨福克郡整体上给人一种城郊感，在萨福克郡，有很多酒吧，咖啡馆，餐馆和公园。许多年轻的高文化层次的专业人士居住在这里，居民的政治倾向整体偏保守。<\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">交通便利，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>距离肯尼迪国际机场2小时，距离纽约城两个半小时。<\\/strong><\\/span>学生可以近距离接触纽约城，纽约在商业和金融的方面发挥着巨大的影响力。纽约的金融区以曼哈顿下城及华尔街为龙头，被称为世界的金融中心，世界500强企业中，有56家总部企业位于纽约。曼哈顿的唐人街是西半球最为密集的华人集中地。纽约还拥有哥伦比亚大学、纽约大学、洛克菲勒大学等名校。<\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">在美国，最适宜户外活动的城市中，排名第43，在<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>前2%的排名；<\\/strong><\\/span>在纽约，最适宜户外活动的城镇中，排名<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">第2；最适宜家庭的城镇中排名第7。<\\/span><\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"color: rgb(63, 63, 63);\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\">该地区为富人区，当地的家庭人均收入为$92,838，远高于<\\/span><strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\">全美平均水平<\\/span><\\/strong><\\/span><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">（55,322）。当地的居民高等教育水平大致在美国平均水平，如研究生及以上学历居民占<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>16%<\\/strong><\\/span>（全美平均12%），本科以上学历居民占<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>19%<\\/strong><\\/span>（全美平均19%），专科学历水平居民仅占<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>27%<\\/strong><\\/span>(全美平均29%）。<\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该社区环境安全，治安良好，犯罪率远远低于美国平均水平。<\\/span><\\/p><p><br\\/><\\/p>\"}', 14, 1548296849);
INSERT INTO `clt_naae_media` VALUES (277, 55, '反馈·Feedback', '{\"id\":\"277\",\"pics_15\":\"[\\\"\\/uploads\\/20190124\\/naae\\/e1413806713b208614074f5e3778b9f6.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/8543e01db581ea996ce723293d67bdb5.jpg\\\"]\",\"page\":\"15\",\"title_catalog\":\"反馈·Feedback\",\"fifteen_text\":\"\",\"fifteen_pic_top\":\"\\/uploads\\/20190124\\/naae\\/6b270ed827b2bc18fec51476fecc4de6.png\",\"fifteen_pic_low\":\"\\/uploads\\/20190124\\/naae\\/327c7147ff37284b30e347ca257483dd.jpg\",\"content15\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-align: justify; text-indent: 2em;\\\">在读学生及家长整体评价为<\\/span><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-align: justify; text-indent: 2em; color: rgb(63, 63, 63);\\\"><strong>4.7，<\\/strong><\\/span><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-align: justify; text-indent: 2em;\\\">其中<\\/span><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; text-align: justify; text-indent: 2em; color: rgb(63, 63, 63);\\\"><strong>五星评价占75%，四星评价占25%。<\\/strong><\\/span><\\/p>\"}', 15, 1548297117);
INSERT INTO `clt_naae_media` VALUES (278, 55, '学校费用·Tuition', '{\"id\":\"\",\"pics_16\":\"[\\\"\\/uploads\\/20190124\\/naae\\/7907bfef59d25543339c5d683d09b0b9.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/240597c976b327ba66b870d8b79cec45.jpg\\\"]\",\"page\":\"16\",\"title_catalog\":\"学校费用·Tuition\",\"sixteen_two_level_title\":\"61200美金\\/年\",\"sixteen_summary\":\"该费用包括学费、寄宿费、餐费。此报价为预估费用，每年可能会有5%-10%的涨幅，具体以invoice为准。（此外学生还需缴纳入学指导费、保险、注册费，活动费、技术费、交通费、健康服务费、书费、校服费以及其他杂费预计20855美金）。\"}', 16, 1548297361);
