INSERT INTO `clt_naae_media` VALUES (289, 58, '学校认证·School Accredit', '{\"id\":\"\",\"pics_4\":\"[\\\"\\/uploads\\/20190124\\/naae\\/0ae98eac5e3cd9fde3f0a172d73ddebb.JPG\\\",\\\"\\/uploads\\/20190124\\/naae\\/61266525c5c6b9f923d9b8a65c6ef7dc.JPG\\\"]\",\"page\":\"4\",\"title_catalog\":\"学校认证·School Accredition\",\"authentication_one\":\"西部学校协会\",\"authentication_two\":\"国家天主教教育协会\",\"authentication_three\":\"西部天主教教育协会\",\"authentication_four\":\"大学理事会\",\"authentication_five\":\"全国大学招生咨询协会和西部大学招生咨询协会\",\"authentication_six\":\"加州高中篮球联赛冠军\",\"four_top_pic\":\"\\/uploads\\/20190124\\/naae\\/afb842aba44c469d6824221c0cbf0480.jpg\",\"four_right_pic\":\"\\/uploads\\/20190124\\/naae\\/74a758a043949da02d68933108f7e26b.jpg\"}', 4, 1548308507);
INSERT INTO `clt_naae_media` VALUES (290, 58, '推荐理由·Recommended Rea', '{\"id\":\"\",\"pics_5\":\"[\\\"\\/uploads\\/20190124\\/naae\\/20c7a631b702620e9a7e3fdc7fe0f283.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/268b067f81a40b256e67ac780cb67bfb.jpg\\\"]\",\"page\":\"5\",\"title_catalog\":\"推荐理由·Recommended Reasons\",\"five_text\":\"\",\"content5\":\"<p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">主教阿莱玛尼高中建立于1956年，是<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(192, 0, 0);\\\"><strong>南加州第二大私立学校，<\\/strong><\\/span>距离加州大学洛杉矶分校20分钟车程，距离南加州大学30分钟车程，距离加州理工学院30分钟车程，距离洛杉矶市中心和机场均30分钟车程。<\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校提供<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(192, 0, 0);\\\"><strong>9门荣誉课程和20门AP课程<\\/strong><\\/span>（其中包含APCapstone Diplomaprogram），所有课程均采用专门的授课系统，因此要求学生使用IPAD或者其他电子设备,学校免费提供IPAD但建议学生买保险以防丢失或者损坏。学校还为国际学生提供了<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(192, 0, 0);\\\"><strong>不同等级的ESL课程，<\\/strong><\\/span>从而让不同的学生更熟练的掌握英语写作，英语听说等技巧。<\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校的<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(192, 0, 0);\\\"><strong>体育是强项。<\\/strong><\\/span>学校组织多种多样的体育活动：足球，越野，高尔夫，网球，水球，排球，篮球，摔跤，棒球，垒球，游泳，田径。学校篮球队曾经获得过<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(192, 0, 0);\\\"><strong>加州高中篮球联赛冠军；<\\/strong><\\/span>课外活动丰富，社团众多。不管什么样的学生都可以在学校找到自己的一席之地，社团包括运动类，艺术类，学习类，领导力类，多元化社团类。&nbsp; &nbsp;<\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校升学率可达<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(192, 0, 0);\\\"><strong>98%，<\\/strong><\\/span>学生升入<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(192, 0, 0);\\\"><strong>加州大学系统<\\/strong><\\/span>各分校，包括加州大学伯克利分校和加州大学洛杉矶分校，以及其他美国名校，包括：哈佛大学，麻省理工学院，康奈尔大学，布朗大学，杜克大学，约翰霍普金斯大学等。<\\/span><\\/p><p><br\\/><\\/p>\"}', 5, 1548308656);
INSERT INTO `clt_naae_media` VALUES (291, 58, '学术特色·Academic Featur', '{\"id\":\"\",\"pics_6\":\"[\\\"\\/uploads\\/20190124\\/naae\\/53f5e36421edbe763a558123253c8e11.JPG\\\",\\\"\\/uploads\\/20190124\\/naae\\/5e4ef91b51abe5753b05c04a412ad7c6.jpg\\\"]\",\"page\":\"6\",\"title_catalog\":\"学术特色·Academic Features\",\"six_text\":\"\",\"content6\":\"<p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-size: 14px; font-family: 宋体, SimSun;\\\">学校为国际学生提供了<span style=\\\"font-size: 14px; font-family: 宋体, SimSun; color: rgb(192, 0, 0);\\\"><strong>不同等级的ESL课程，<\\/strong><\\/span>从而让不同的学生更熟练的掌握英语写作，英语听说等技巧。学生会被安排在ESL小班课堂上学习，并在其他课堂上与本地学生一起学习。英语词汇，语法，写作、阅读、口语技巧和综合技巧将被传授给学生们。小班教学位学生们提供了良好的英语演讲，讨论的机会，从而提高学生的英语能力。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-size: 14px; font-family: 宋体, SimSun;\\\">学校提供9门荣誉课程和<span style=\\\"font-size: 14px; font-family: 宋体, SimSun; color: rgb(192, 0, 0);\\\"><strong>20门AP课程，<\\/strong><\\/span>以高教学质量闻名，2018年有12名学生获得了APCapstoneDiploma。周末还提供<span style=\\\"font-size: 14px; font-family: 宋体, SimSun; color: rgb(192, 0, 0);\\\"><strong>SATPrep课程，<\\/strong><\\/span>学生可以按需参加，新SAT平均分高达<span style=\\\"font-size: 14px; font-family: 宋体, SimSun; color: rgb(192, 0, 0);\\\"><strong>1350<\\/strong><\\/span>（前20%）。<\\/span><\\/p>\"}', 6, 1548308826);
