INSERT INTO `clt_naae_media` VALUES (298, 58, '课程设置·Curriculum', '{\"id\":\"\",\"page\":\"8\",\"title_catalog\":\"课程设置·Curriculum\",\"eight_pic\":\"\\/uploads\\/20190124\\/naae\\/0c3dc6241355dbd94f0ae89d28d1fef1.jpg\",\"z_d_y_data\":\"{\\\"表演艺术\\\":\\\"戏剧~进阶戏剧~剧本书写~数码视频制作~进阶数码视频制作~合唱~进阶合唱~舞蹈A,B~乐队初级~进阶乐队~进阶交响乐~初级交响乐~鼓\\\",\\\"外语\\\":\\\"西班牙语1\\\\\\/2\\\\\\/3~标准西班牙语1\\\\\\/2~法语1\\\\\\/2\\\\\\/3~中文普通话1\\\\\\/2\\\\\\/3\\\",\\\"AP课程\\\":\\\"英语语言~英语文学~微积分AB\\\\\\/BC~生物~化学~物理1\\\\\\/C~世界历史~美国历史~美国政府与政治~宏观经济学~西班牙语言~西班牙文学~统计学~调研~研讨~计算机科学A~绘画~2D设计\\\",\\\"荣誉课程\\\":\\\"美国文学10~英语文学11~代数1\\\\\\/2~几何~三角函数~化学~英语9~西班牙语3~宗教课程\\\"}\"}', 8, 1548309471);
INSERT INTO `clt_naae_media` VALUES (299, 58, '体育特色·Sports Activiti', '{\"pics_9_top\":\"[\\\"\\/uploads\\/20190124\\/naae\\/36e70e0a49a310100ca8f94e94dab329.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/5a60092ab94c86c1f09dea6102fc6d80.jpg\\\"]\",\"pics_9_low\":\"[\\\"\\/uploads\\/20190124\\/naae\\/5744d3bb496d112387a0f2241682479d.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/38f7992d2d7d8fd7a0da9fd54d378abf.jpg\\\"]\",\"id\":\"\",\"pics_9\":\"\",\"page\":\"9\",\"title_catalog\":\"体育特色·Sports Activities\",\"z_d_y_data\":\"{\\\"秋季\\\":\\\"男子橄榄球，男子越野，男子水球，女子网球，女子高尔夫，女子排球，女子越野\\\",\\\"冬季\\\":\\\"男子摔跤，男子篮球，男子足球，女子篮球，女子足球，女子水球\\\",\\\"全年\\\":\\\"啦啦队，舞蹈\\\"}\"}', 9, 1548309582);
INSERT INTO `clt_naae_media` VALUES (300, 58, '艺术特色·Fine Arts', '{\"id\":\"\",\"pics_10\":\"[\\\"\\/uploads\\/20190124\\/naae\\/ae381dbd96b2dfbc69a54de45187517f.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/4c9e64278330ec367292f5624025449b.JPG\\\"]\",\"page\":\"10\",\"title_catalog\":\"艺术特色·Fine Arts\",\"ten_pic\":\"\\/uploads\\/20190124\\/naae\\/9cbf6800580f767ef5f107b3856e70fc.jpg\",\"z_d_y_data\":\"{\\\"舞蹈\\\":\\\"学校开设专门的舞蹈课程，分类详细,聘请了专业的舞蹈老师，舞蹈A\\\\\\/B；高级舞蹈；嘻哈舞蹈；舞蹈团。\\\",\\\"器乐\\\":\\\"学校有专业的高中管弦乐团和乐队，包含小提琴、中提琴、大提琴、低音提琴等乐器；学校非常重视音乐教育，因为经过音乐培训的学生在考试中更容易取得更高的分数；专门设有初级管弦乐队，给没有音乐基础的孩子提供表演机会。\\\",\\\"合唱团\\\":\\\"SCHOLA Show合唱团\\\",\\\"戏剧表演\\\":\\\"开设多门戏剧表演课程；提供如下戏剧活动：夏日独角戏、收获的秋季、圣诞表演、独角戏、春季音乐剧、高级戏剧沙盒以及戏剧社。\\\",\\\"视觉艺术\\\":\\\"每年春季，学校都会在校友会上举办阿莱马尼视觉艺术展，许多室内艺术、雕塑、陶瓷等高级艺术都会在此展出。超过七百件的艺术品都是由这些零基础学习艺术的学生创作而成。\\\"}\"}', 10, 1548309723);
INSERT INTO `clt_naae_media` VALUES (301, 58, '学生社团·Club Activities', '{\"id\":\"301\",\"pics_11\":\"[\\\"\\/uploads\\/20190124\\/naae\\/172a31c9fa4d9d29004a454d43acdc82.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/c09de4cf32ac5552477d70b2688d4159.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/32973c1870a3496deb2804e624e1636c.jpg\\\"]\",\"page\":\"11\",\"title_catalog\":\"学生社团·Club Activities\",\"eleven_summary\":\"长跑俱乐部，学术全能俱乐部，校友会，乐队与交响乐，社区合唱，乐器，机器人，学生新闻广播，动漫俱乐部，学生会，荣誉守卫，加州奖学金联盟，天主教服务俱乐部，华盛顿之旅，戏剧俱乐部，电子运动，国际学生俱乐部，分享俱乐部，国家荣誉协会，SADD，SCHOLA表演合唱，毕业生协会，拉丁裔荣誉协会，高二学生协会，演讲与辩论，酷学生俱乐部，周报，学校精神俱乐部，年鉴。\"}', 11, 1548309925);
INSERT INTO `clt_naae_media` VALUES (302, 58, '学校设施·Facilities', '{\"id\":\"\",\"pics_12\":\"[\\\"\\/uploads\\/20190124\\/naae\\/f71f86383768342fa209cc671cbc8bda.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/49f2ba4127a484efa48b57d932379649.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/f1fc3b8e23076b9a003bfe711599c1c0.png\\\"]\",\"page\":\"12\",\"title_catalog\":\"学校设施·Facilities\",\"z_d_y_data\":\"{\\\"室外\\\":\\\"田径场,橄榄球场,室外游泳池,足球场,棒球场,网球场。\\\",\\\"室内\\\":\\\"篮球场和排球场,戏剧中心,图书馆,3所教学楼,学生食堂。图书馆媒体中心提供强大的数据库,微缩资料,试听材料,电子书籍等。学校提供无线上网,学生配备现代化的洗浴设施和学生活动室。\\\"}\"}', 12, 1548310056);
INSERT INTO `clt_naae_media` VALUES (303, 58, '大学升学情况·University-En', '{\"id\":\"\",\"pics_13\":\"\",\"page\":\"13\",\"title_catalog\":\"大学升学情况·University-Entrance Status\",\"thirteen_summary\":\"学校整体升学率可达98%，该校学生申请最多的大学有：\",\"thirteen_pic_top\":\"\\/uploads\\/20190124\\/naae\\/7b2df40fa9a91728f330e5216129ffcc.png\",\"thirteen_pic_low\":\"\\/uploads\\/20190124\\/naae\\/ab0976432bdd5a1e9c834164d3b2cca1.jpg\"}', 13, 1548310233);
INSERT INTO `clt_naae_media` VALUES (304, 58, '社区概况·Community Profi', '{\"id\":\"\",\"pics_14\":\"[\\\"\\/uploads\\/20190124\\/naae\\/a4ab7705d29f868bee6748bf54ade4ff.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/dd9406cd0ca9054b72ccbd55f97d9b37.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/7d013dd51eae3dbc043568c7f1dd6b03.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/170c1f10e8e54f1f6c1a1c4e7b02b44e.png\\\"]\",\"page\":\"14\",\"title_catalog\":\"社区概况·Community Profile\",\"fourteen_text\":\"\",\"content14\":\"<p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">主教阿莱玛尼高中位于洛杉矶北部，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(192, 0, 0);\\\"><strong>距离加州大学洛杉矶分校20分钟车程，距离南加州大学30分钟车程，距离加州理工学院30分钟车程，距离洛杉矶市中心和机场均30分钟车程。<\\/strong><\\/span>洛杉矶是加州第一大城市也是全美第二大城市，常被称为“天使之城”，更是全世界重要的工商业、国际贸易、科教、娱乐和体育中心之一。好莱坞、加州迪士尼乐园、环球影城等等都位于洛杉矶，南加州的奢华之城比佛利山庄也坐落在洛杉矶的中心地带，众多国际名流居住于此。学校位于的社区治安很好，气候环境极佳，另外，健全的医疗健康体系、发达的交通系统和丰富的夜生活都是这个现代化大都市具备的优势。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">洛杉矶在Niche网站中获取的关注度也是非常高，现有870+的评论，整体评级为4星，其中4星以上好评达到74%，多数当地居民认为洛杉矶的文化非常具有包容性。<\\/span><\\/p><p><br\\/><\\/p>\"}', 14, 1548310740);
INSERT INTO `clt_naae_media` VALUES (305, 58, '反馈·Feedback', '{\"id\":\"\",\"pics_15\":\"[\\\"\\/uploads\\/20190124\\/naae\\/9f193c8061513985d744456d619d5d3a.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/deca2b7bd09b0b86253635fef7d288a3.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/e389a055721ca74382934eecc60d331d.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/5cb1de39cf86b96074ce82de4147a1f5.png\\\"]\",\"page\":\"15\",\"title_catalog\":\"反馈·Feedback\",\"fifteen_text\":\"\",\"fifteen_pic_top\":\"\\/uploads\\/20190124\\/naae\\/f69c1d83f7598ccfc818ca19f2456844.png\",\"fifteen_pic_low\":\"\\/uploads\\/20190124\\/naae\\/1bfb6a8600dfd67537626568acbdd571.jpg\",\"content15\":\"<p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该学校在NICHE网站现有<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(192, 0, 0);\\\"><strong>223条评论，获取关注度极高。<\\/strong><\\/span>在读学生及家长整体评价为4星，其中四星以上评价达70%。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"color: rgb(192, 0, 0);\\\"><strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">97%<\\/span><\\/strong><\\/span><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">的学生和家长认为本校学生都非常具有运动精神，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(192, 0, 0);\\\"><strong>92%<\\/strong><\\/span>的学生在学校感觉十分安全。<\\/span><\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"color: rgb(192, 0, 0);\\\"><strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">86%<\\/span><\\/strong><\\/span><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">的学生认为本校同学都非常具有竞争力，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(192, 0, 0);\\\"><strong>83%<\\/strong><\\/span>的学生在学校生活感到幸福。<\\/span><\\/p>\"}', 15, 1548310857);
