INSERT INTO `clt_naae_media` VALUES (310, 60, '学校概况·Overview', '{\"id\":\"\",\"page\":\"2\",\"title_catalog\":\"学校概况·Overview\",\"two_pic\":\"\\/uploads\\/20190124\\/naae\\/0b3bdd1c357952b40dcdb10e59413ea1.jpg\",\"z_d_y_data\":\"{\\\"建校时间\\\":\\\"1979年\\\",\\\"学生人数\\\":\\\"458人\\\",\\\"占地面积\\\":\\\"50英亩\\\",\\\"宗教背景\\\":\\\"基督教\\\",\\\"周边城市\\\":\\\"波士顿\\\",\\\"学校性质\\\":\\\"混校\\\",\\\"年级设置\\\":\\\"PK，K-12年级\\\",\\\"招生年级\\\":\\\"9-12年级（7、8年级需要提前沟通）\\\",\\\"住宿类型\\\":\\\"走读\\\",\\\"师生比例\\\":\\\"1:9\\\",\\\"国际学生\\\":\\\"13%\\\",\\\"课堂规模\\\":\\\"14人\\\",\\\"高级教师\\\":\\\"60%\\\",\\\"SAT均分\\\":\\\"1270\\\",\\\"AP课程\\\":\\\"15门\\\",\\\"入学要求\\\":\\\"TOEFL\\\",\\\"学校官网\\\":\\\"www.pcaschool.org\\\",\\\"申请费\\\":\\\"250美金\\\"}\"}', 2, 1548314740);
INSERT INTO `clt_naae_media` VALUES (311, 60, '学校排名·School Ranking', '{\"id\":\"311\",\"pics_3\":\"[\\\"\\/uploads\\/20190124\\/naae\\/7d9107e3ee9a41cd582ca0ef7dbeb8a0.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/01a2ed7e4ebc030faecaed0a0438b9a3.png\\\"]\",\"page\":\"3\",\"title_catalog\":\"学校排名·School Ranking\",\"ranking_one\":\"美国权威高中排名，Niche排名A类院校\",\"ranking_two\":\"全美基督教高中排名前14%\",\"ranking_three\":\"新汉普郡私立K-12学校排名第1\",\"three_top_pic\":\"\\/uploads\\/20190124\\/naae\\/114a97530868e80a96062f9e5e2b0f40.png\",\"three_low_pic\":\"\\/uploads\\/20190124\\/naae\\/ad6a14ee3883f86671b487c671719633.jpg\"}', 3, 1548314878);
INSERT INTO `clt_naae_media` VALUES (312, 60, '学校认证·School Accredit', '{\"id\":\"312\",\"pics_4\":\"[\\\"\\/uploads\\/20190124\\/naae\\/8f03ff440541e9b6470878b83bc522dd.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/c602dc640ed6a180ed62ba18d6b68d9a.jpg\\\"]\",\"page\":\"4\",\"title_catalog\":\"学校认证·School Accredition\",\"authentication_one\":\"新英格兰学院和学校协会成员\",\"authentication_two\":\"基督教学校协会(ASCI)\",\"authentication_three\":\"2003年,2006年和2010年的州篮球锦标赛冠军\",\"authentication_four\":\"棒球(2010年)锦标赛冠军\",\"authentication_five\":\"田径比赛中的个人国家冠军\",\"authentication_six\":\"NHIAA体育精神锦标赛冠军\",\"four_top_pic\":\"\\/uploads\\/20190124\\/naae\\/43962a29ca29f2c033b214fb81ca436c.jpg\",\"four_right_pic\":\"\\/uploads\\/20190124\\/naae\\/511ce8f6cca711e613349ec20a258dfe.jpg\"}', 4, 1548314878);
INSERT INTO `clt_naae_media` VALUES (313, 60, '推荐理由·Recommended Rea', '{\"id\":\"313\",\"pics_5\":\"[\\\"\\/uploads\\/20190124\\/naae\\/56510dceec26ab8584518ff5ff72b5cf.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/92e406e8f8e53c272c11dbf3c5b91cd2.jpg\\\"]\",\"page\":\"5\",\"title_catalog\":\"推荐理由·Recommended Reasons\",\"five_text\":\"\",\"content5\":\"<p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">课程非常丰富，提供上百门基础课程，提供超过<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(255, 0, 0);\\\"><strong>20个荣誉和AP课程，<\\/strong><\\/span>使学生能够很好地为大学录取过程做好准备，以及提供STEM(科学技术工程和数学)项目，机器人，人工智能实力突出。学校还开设了学心理学，海洋生物学、解刨学等特色课程。<\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">&nbsp;<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(255, 0, 0);\\\"><strong>英语学习者计划，<\\/strong><\\/span>是国际学生的一个重要部分，所有ELL学生都可以根据需要参加ELL课程，以培养他们的英语语言技能，重点是培养每个学生的口语，听力，阅读，写作和理解能力。<\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"color: rgb(255, 0, 0);\\\"><strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">大学指导，<\\/span><\\/strong><\\/span><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">工作人员为学生进入最好的大学提供了极好的支持，指导办公室主任专门协助学生选择合适的大学，以实现他们的个人目标。除了学术支持外，还为有兴趣提高大学申请流程中考试成绩的学生<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(255, 0, 0);\\\"><strong>提供SAT和ACT准备。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">双重认证，<\\/span><\\/strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">获得了国际基督教学校协会（ASCI）和新英格兰学校和学院协会（NEASC）的认可，两者都对我们整个学校运营的各个方面进行了非常严格的评估。<\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">开发了艺术课程，以最好地满足学生在大学阶段进一步学习的需求，课程将<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(255, 0, 0);\\\"><strong>多达八个视觉艺术课程与音乐和戏剧课程相结合，创造出多元化的课程。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">地理位置优越，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(255, 0, 0);\\\"><strong>距离市中心仅12分钟车程，紧邻新罕布什尔大学。<\\/strong><\\/span><\\/span><\\/p><p><br\\/><\\/p>\"}', 5, 1548315041);
INSERT INTO `clt_naae_media` VALUES (314, 60, '学术特色·Academic Featur', '{\"id\":\"314\",\"pics_6\":\"[\\\"\\/uploads\\/20190124\\/naae\\/e32a453a4b7ee505a59cd30b494e071b.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/3f8fe32c6a28ff37d55d8b977fa19822.jpg\\\"]\",\"page\":\"6\",\"title_catalog\":\"学术特色·Academic Features\",\"six_text\":\"\",\"content6\":\"<p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">Niche调查中，学校学术评级是<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(255, 0, 0);\\\"><strong>A级别，学校SAT均分1270，ACT28分，<\\/strong><\\/span>远高于美国平均水平（全美SAT均分1080，ACT均分23）。<\\/span><\\/p>\"}', 6, 1548315250);
INSERT INTO `clt_naae_media` VALUES (315, 60, '课程设置·Curriculum', '{\"id\":\"315\",\"page\":\"7\",\"title_catalog\":\"课程设置·Curriculum\",\"seven_pic\":\"\\/uploads\\/20190124\\/naae\\/aa70a3f3c203cc5feb19ec84319b0d0b.jpg\",\"z_d_y_data\":\"{\\\"AP课程\\\":\\\"英语语言~微积分AB~微积分BC~统计学~生物~化学~物理\\\",\\\"圣经\\\":\\\"旧约的调查~新约的调查~基督教护教学~世界观与伦理~教会历史~系统神学~基督徒~生活~使命与世界宗教~圣经预言\\\",\\\"英语\\\":\\\"修辞学与写作~分析写作~美国写作~伟大的文学作品~表彰伟大的文学作品~英语语言学习者~阅读流畅性~国际英语~中级国际英语~基督教概论\\\",\\\"艺术\\\":\\\"新生研讨课~绘画I~图纸画二世~艺术的元素~设计的原则~版画~平面设计~数码摄影~年鉴和出版物设计~中世纪的洞穴~文艺复兴到现代~美联社艺术史~美联社工作室艺术~声音~交响乐团~吉他~吉他合奏~音乐欣赏~音乐理论~美联社音乐理论~戏剧艺术概论~舞台表演\\\",\\\"外语\\\":\\\"普通话\\\"}\"}', 7, 1548315386);
INSERT INTO `clt_naae_media` VALUES (316, 60, '课程设置·Curriculum', '{\"id\":\"316\",\"page\":\"8\",\"title_catalog\":\"课程设置·Curriculum\",\"eight_pic\":\"\\/uploads\\/20190124\\/naae\\/7e5a87c2ee6f457bc4a80c2a1309a5b9.png\",\"z_d_y_data\":\"{\\\"历史\\\\\\/社会科学\\\":\\\"公民政府~经济学~世界历史~美国历史~纪念美国历史~纪念第二次世界大战的历史~纪念冷战的历史~美联社欧洲历史\\\",\\\"数学\\\":\\\"微积分AB ~微积分BC~统计数据~大学数学应用课题~个人理财基础\\\",\\\"科学\\\":\\\"环境科学~海洋科学~解剖学和生理学~物理概念~心理学\\\",\\\"技术\\\":\\\"机器人~软件工程~计算机编程\\\",\\\"荣誉课程\\\":\\\"旧约调查~新约调查~分析写作~美国文学~声音~法语~西班牙语~世界历史~欧洲历史~代数~几何~微积分~生物学~化学\\\"}\"}', 8, 1548315481);
INSERT INTO `clt_naae_media` VALUES (317, 60, '体育特色·Sports Activiti', '{\"pics_9_top\":\"[\\\"\\/uploads\\/20190124\\/naae\\/e72b8d3c7688237720fd198be9355a3e.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/a8ce9c8efb378c6fec87e0bca00de03b.png\\\"]\",\"pics_9_low\":\"[\\\"\\/uploads\\/20190124\\/naae\\/f7f7ec6044619ca3d2ddcb0fa16ac29e.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/33eb74c3c339f18176b0d0c2a0bd5d60.png\\\"]\",\"id\":\"317\",\"pics_9\":\"\",\"page\":\"9\",\"title_catalog\":\"体育特色·Sports Activities\",\"z_d_y_data\":\"{\\\"秋季运动\\\":\\\"JH越野赛，越野赛，JH男子足球，JH女子足球，男子校队足球，女子校队足球队，JH JV排球，JH校队排球，JV排球队\\\",\\\"冬季运动\\\":\\\"女子篮球，男子篮球，JH女子篮球，JH男子篮球，JV女子篮球，JV男子篮球校，队女子篮球校，队男子篮球校队，冬季赛道\\\",\\\"春季运动\\\":\\\"校队棒球，校队垒球，校队田径，校队男子网球，JV棒球，JH棒球，JH垒球，JH田径\\\",\\\"荣誉\\\":\\\"2003年，2006年和2010年的州篮球锦标赛冠军；棒球(2010年）锦标赛冠军；田径比赛中的个人国家冠军以及NHIAA体育精神锦标赛冠军。\\\"}\"}', 9, 1548316020);
INSERT INTO `clt_naae_media` VALUES (318, 60, '艺术特色·Fine Arts', '{\"id\":\"318\",\"pics_10\":\"[\\\"\\/uploads\\/20190124\\/naae\\/1e579eac229abaf51d69532b49e6d195.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/0f1d27ffddcafb06cdca9eb5008190c5.jpg\\\"]\",\"page\":\"10\",\"title_catalog\":\"艺术特色·Fine Arts\",\"ten_pic\":\"\\/uploads\\/20190124\\/naae\\/fa11087d9db06b9a5f0178c115d6dd46.png\",\"z_d_y_data\":\"{\\\"艺术特色\\\":\\\"旨在丰富学生的艺术技能，并满足那些有兴趣在大学继续深造的学生的需求。该计划旨在最大限度地包括声乐和器乐，戏剧和视觉艺术的学习课程。\\\",\\\"视觉艺术课程\\\":\\\"整合了美学，艺术批评，艺术史和艺术创作的研究。学生参与艺术创作，观看和讨论艺术，阅读和写作艺术，了解艺术创作的背景，以及思考艺术的基本问题,包括观察绘画，摄影，版画和设计。\\\",\\\"音乐\\\":\\\"管弦乐队、乐队、吉他和合唱团，音乐系每年都有学生有资格参加全州大赛。\\\",\\\"剧院\\\":\\\"熟悉戏剧，制作，动作和舞蹈，学生还有机会参与后台和幕后活动。\\\"}\"}', 10, 1548316616);
INSERT INTO `clt_naae_media` VALUES (319, 60, '学生社团·Club Activities', '{\"id\":\"319\",\"pics_11\":\"[\\\"\\/uploads\\/20190124\\/naae\\/027a4165bd8a539d42643f22c8a7c533.jpg\\\",\\\"\\/uploads\\/20190124\\/naae\\/3c5320dac3821d523617232bf16183a2.png\\\",\\\"\\/uploads\\/20190124\\/naae\\/b86984ddda604cfaa71701001fca3a23.png\\\"]\",\"page\":\"11\",\"title_catalog\":\"学生社团·Club Activities\",\"eleven_summary\":\"活动：舞会，年鉴，社交舞蹈，学校精神活动，戏剧，音乐，咖啡馆，年度篝火等等。\\r\\n俱乐部：崇拜团队，国际象棋俱乐部，测验俱乐部，国际俱乐部，数学团队，宣教俱乐部，长笛合唱团，学生会，舞会，祝福，圣经研究和晨祷团，Pep俱乐部，科技俱乐部，健身俱乐部和技术俱乐部。\"}', 11, 1548317234);
INSERT INTO `clt_naae_media` VALUES (320, 60, '学校设施·Facilities', '{\"id\":\"320\",\"pics_12\":\"[\\\"\\/uploads\\/20190124\\/naae\\/c0a560193dc1ac2b4d5adbcbb12f74f5.JPG\\\",\\\"\\/uploads\\/20190124\\/naae\\/01a1edf0c6b4e5ea40f0d1d452f8cbbe.jpeg\\\",\\\"\\/uploads\\/20190124\\/naae\\/5b18618f6806e8874618cc164a64e888.jpg\\\"]\",\"page\":\"12\",\"title_catalog\":\"学校设施·Facilities\",\"z_d_y_data\":\"{\\\"室内\\\":\\\"餐厅，计算机实验室，室内运动房，图书馆，音乐室，自然科学实验室。\\\",\\\"室外\\\":\\\"运动场，网球场。\\\"}\"}', 12, 1548317386);
