INSERT INTO `clt_naae_media` VALUES (340, 62, '体育特色·Sports Activiti', '{\"pics_9_top\":\"[\\\"\\/uploads\\/20190125\\/naae\\/ae2fa7c27c81b8ecabedfa18d0030a3b.jpg\\\",\\\"\\/uploads\\/20190125\\/naae\\/082a89bb852aeb195d8991800f68e827.jpg\\\"]\",\"pics_9_low\":\"[\\\"\\/uploads\\/20190125\\/naae\\/b0a10aef3e68d8d2bfdfd3953595762c.jpg\\\",\\\"\\/uploads\\/20190125\\/naae\\/a7ee77fc552a5a3ca2d66f89964ef480.jpg\\\"]\",\"id\":\"\",\"pics_9\":\"\",\"page\":\"9\",\"title_catalog\":\"体育特色·Sports Activities\",\"z_d_y_data\":\"{\\\"所获奖项\\\":\\\"如男女足球，篮球和棒球等联赛及新英格兰体育锦标赛冠军、黄蜂队取得了20胜5负的战绩，赢得了FairchesterLeague常规赛和季后赛冠军，成为B级NEPSAC锦标赛冠军。\\\",\\\"体育项目\\\":\\\"越野赛，陆上曲棍球，橄榄球，足球，排球，篮球，舞蹈，冰上曲棍球，壁球，游泳，摔跤，棒球，高尔夫球，长曲棍球，垒球，网球。\\\"}\"}', 9, 1548399055);
INSERT INTO `clt_naae_media` VALUES (341, 62, '艺术特色·Fine Arts', '{\"id\":\"\",\"pics_10\":\"[\\\"\\/uploads\\/20190125\\/naae\\/fd98d7aadc3d8ce2b6a67fac5ef54838.jpg\\\",\\\"\\/uploads\\/20190125\\/naae\\/bd52b6738b21a4ad3acee89f55098092.jpg\\\"]\",\"page\":\"10\",\"title_catalog\":\"艺术特色·Fine Arts\",\"ten_pic\":\"\\/uploads\\/20190125\\/naae\\/deb8264bf37787eda2a552d473612de1.jpg\",\"z_d_y_data\":\"{\\\"视觉艺术\\\":\\\"绘画，陶瓷，雕塑，版画，平面设计和插图以及大学先修艺术史、新媒体艺术，壁画和其他新类型的独立研究和高级项目。\\\",\\\"音乐\\\":\\\"音乐理论、合唱、序曲、爵士乐团、乐队、表演。\\\",\\\"剧院\\\":\\\"角色发展，阻挡，布景设计，编舞，服装，方向，场景工作和排练技巧。\\\",\\\"\\\":\\\"声乐和器乐课程因每年入选CT和全州音乐节的学生人数而获得国家认可。\\\"}\"}', 10, 1548399195);
INSERT INTO `clt_naae_media` VALUES (342, 62, '学生社团·Club Activities', '{\"id\":\"\",\"pics_11\":\"[\\\"\\/uploads\\/20190125\\/naae\\/9430659b7e980a5da9c0f9aeea2c0bcf.jpg\\\",\\\"\\/uploads\\/20190125\\/naae\\/43974ee596c36238cc1ca114114a3869.jpg\\\",\\\"\\/uploads\\/20190125\\/naae\\/1f3825d9684eac597f66d721b3670064.jpg\\\"]\",\"page\":\"11\",\"title_catalog\":\"学生社团·Club Activities\",\"eleven_summary\":\"无伴奏合唱团、学生报社、艺术社、动漫社、棋艺社、生物奥林匹克俱乐部、象棋俱乐部、合唱团、计算机科学俱乐部、保护社、辩论队、多样性社团、文学杂志、毽子俱乐部、即兴表演社、扶轮服务团、少年政治家、语言学奥林匹斯俱乐部、数学队、模拟联合国、制片俱乐部、音乐社区服务俱乐部、乐团、哲学社、科幻小说俱乐部、滑雪俱乐部、诗社、运动俱乐部、学生会、学生音乐家联盟、兵乓球社、戏剧俱乐部、年鉴。\"}', 11, 1548399243);
INSERT INTO `clt_naae_media` VALUES (343, 62, '学校设施·Facilities', '{\"id\":\"\",\"pics_12\":\"[\\\"\\/uploads\\/20190125\\/naae\\/ebec9d8fa800ee2594b854be304a1f58.jpg\\\",\\\"\\/uploads\\/20190125\\/naae\\/37bc262b7b47d4ea2464f621bc59fc98.jpg\\\",\\\"\\/uploads\\/20190125\\/naae\\/6bf38ba0476150d1513d47a1755b6784.jpg\\\"]\",\"page\":\"12\",\"title_catalog\":\"学校设施·Facilities\",\"z_d_y_data\":\"{\\\"学校设施\\\":\\\"主要建筑:科学中心，剧场，艺术工作室，三个科技中心，一个图书馆，一个音乐中心（包括合唱排练室，乐队排练室），还有一个30英亩的运动中心（包括三个室内篮球场，室内游泳池，楼上田径跑道，健身房，多功能体操房，学生活动室和自习室，室外足球场，网球场）等。\\\"}\"}', 12, 1548399317);
INSERT INTO `clt_naae_media` VALUES (344, 62, '大学升学情况·University-En', '{\"id\":\"\",\"pics_13\":\"\",\"page\":\"13\",\"title_catalog\":\"大学升学情况·University-Entrance Status\",\"thirteen_summary\":\"100%升学率，该校学生最感兴趣的大学主要为以下几所：\",\"thirteen_pic_top\":\"\\/uploads\\/20190125\\/naae\\/1795726759b7c8782848923a04ee2500.png\",\"thirteen_pic_low\":\"\\/uploads\\/20190125\\/naae\\/f195e9faa976ae213eb8fdb3f191a5f4.jpg\"}', 13, 1548399410);
INSERT INTO `clt_naae_media` VALUES (345, 62, '社区概况·Community Profi', '{\"id\":\"\",\"pics_14\":\"[\\\"\\/uploads\\/20190125\\/naae\\/77892a48b7cc4771768180fb7e596666.jpg\\\",\\\"\\/uploads\\/20190125\\/naae\\/563c32b7c17280e0f70b3316aa04030e.jpg\\\",\\\"\\/uploads\\/20190125\\/naae\\/a7374862cd8dff4e0f5484d9eb3a9f11.png\\\"]\",\"page\":\"14\",\"title_catalog\":\"社区概况·Community Profile\",\"fourteen_text\":\"\",\"content14\":\"<p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">哈姆登被称为“ 沉睡巨人的土地”，2012年，Hamden镇在“美国最佳小城市”的CNN Money榜单中排名第53位。2008年，“财富”杂志和CNNMoney选择Hamden作为他们的“最佳生活和发布地点”排名第33位，理由是Hamden很棒融合了城市和郊区的生活方式，以及哈姆登的特殊教育体系和新英格兰魅力。<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(0, 176, 80);\\\"><strong>距波士顿仅需1小时32分钟的车程，到达机场仅需1小时16分钟。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该地区在<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(0, 176, 80);\\\"><strong>全美<\\/strong><\\/span>多元化城市排名<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(0, 176, 80);\\\"><strong>前9%，<\\/strong><strong>康涅狄格州<\\/strong><\\/span>排名<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(0, 176, 80);\\\"><strong>前10%，<\\/strong><\\/span>城市排名级别<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(0, 176, 80);\\\"><strong>A-。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-align: justify; text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">该地区为富人区，当地的家庭人均财产为228,200，远高于<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(0, 176, 80);\\\"><strong>全美平均水平<\\/strong><\\/span>($184,700)。当地的居民高等教育水平远高于美国平均水平，如研究生及以上学历居民占<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(0, 176, 80);\\\"><strong>24%<\\/strong><\\/span>(全美平均12%)，本科以上学历居民占<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(0, 176, 80);\\\"><strong>21%<\\/strong><\\/span>(全美平均19%)，专科学历水平居民仅占26%(全美平均29%)，高中及以下水平居民仅占29%（全美平均41%）。<\\/span><\\/p>\"}', 14, 1548399689);
INSERT INTO `clt_naae_media` VALUES (346, 62, '反馈·Feedback', '{\"id\":\"\",\"pics_15\":\"[\\\"\\/uploads\\/20190125\\/naae\\/543342d3b7024e462076492fa3028a2e.jpg\\\",\\\"\\/uploads\\/20190125\\/naae\\/980c6073d6d462545b510c598fd426ac.jpg\\\",\\\"\\/uploads\\/20190125\\/naae\\/1a649d2d6c987f30587687031bbe24af.jpg\\\",\\\"\\/uploads\\/20190125\\/naae\\/5c9974d2230f825afd07374991113d92.jpg\\\"]\",\"page\":\"15\",\"title_catalog\":\"反馈·Feedback\",\"fifteen_text\":\"\",\"fifteen_pic_top\":\"\\/uploads\\/20190125\\/naae\\/74c598d646bb4219d5482fafc53a53eb.png\",\"fifteen_pic_low\":\"\\/uploads\\/20190125\\/naae\\/1c0000c7e53688cb1927ed64a54c5de1.jpg\",\"content15\":\"<p style=\\\"text-indent: 2em; text-align: justify;\\\">在读学生及家长整体评价为<span style=\\\"color: rgb(0, 176, 80);\\\"><strong>4.5，<\\/strong><\\/span>其中<span style=\\\"color: rgb(0, 176, 80);\\\"><strong>五星评价占53%，四星评价占34%，<\\/strong><\\/span>三星评价评价占12%，三星以下评价为0。<\\/p><p style=\\\"text-indent: 2em; text-align: justify;\\\"><span style=\\\"color: rgb(0, 176, 80);\\\"><strong>100%<\\/strong><\\/span>的学生认为该校的学习氛围浓厚，<strong><span style=\\\"color: rgb(0, 176, 80);\\\">100%<\\/span><\\/strong>的学生能够严肃对待学业。<\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"color: rgb(0, 176, 80);\\\"><strong><span style=\\\"text-align: justify; text-indent: 2em;\\\">88%<\\/span><\\/strong><\\/span><span style=\\\"text-align: justify; text-indent: 2em;\\\">的学生认为学校的同学很友好、很刻苦；<span style=\\\"text-align: justify; text-indent: 2em; color: rgb(0, 176, 80);\\\"><strong>100%<\\/strong><\\/span>的学生认为该校的学生很积极参与各种活动。<\\/span><\\/p>\"}', 15, 1548400200);
