INSERT INTO `clt_naae_media` VALUES (350, 63, '学校概况·Overview', '{\"id\":\"\",\"page\":\"2\",\"title_catalog\":\"学校概况·Overview\",\"two_pic\":\"\\/uploads\\/20190128\\/naae\\/63a42710a02745cc2ea7103ad484a80f.jpg\",\"z_d_y_data\":\"{\\\"建校时间\\\":\\\"2006年\\\",\\\"学生人数\\\":\\\"560人\\\",\\\"占地面积\\\":\\\"93英亩\\\",\\\"宗教背景\\\":\\\"天主教\\\",\\\"周边城市\\\":\\\"洛杉矶\\\",\\\"学校性质\\\":\\\"私立男女合校\\\",\\\"年级设置\\\":\\\"9-12年级\\\",\\\"招生年级\\\":\\\"9-11年级\\\",\\\"住宿类型\\\":\\\"寄宿家庭\\\",\\\"师生比例\\\":\\\"1:13\\\",\\\"国际学生\\\":\\\"6%\\\",\\\"课堂规模\\\":\\\"23人\\\",\\\"高级教师\\\":\\\"75%以上教师拥有硕士学位\\\",\\\"ESL课程\\\":\\\"有\\\",\\\"SAT均分\\\":\\\"1870\\\",\\\"AP课程\\\":\\\"16门\\\",\\\"入学要求\\\":\\\"TOEFL\\\",\\\"学校官网\\\":\\\"https:\\\\\\/\\\\\\/www.xavierprep.org\\\",\\\"申请费\\\":\\\"100美金\\\"}\"}', 2, 1548642312);
