INSERT INTO `clt_naae_media` VALUES (352, 63, '学校认证·School Accredit', '{\"id\":\"352\",\"pics_4\":\"[\\\"\\/uploads\\/20190128\\/naae\\/1a07bd7f1b548bd9e66ac5491811e194.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/04e607576296ad9ae6135dac987aa3ee.jpg\\\"]\",\"page\":\"4\",\"title_catalog\":\"学校认证·School Accredition\",\"authentication_one\":\"西部天主教教育协会\",\"authentication_two\":\"西部院校协会\",\"authentication_three\":\"全国大学招生咨询协会\",\"authentication_four\":\"全美天主教教育协会\",\"authentication_five\":\"西部天主教教育协会\",\"authentication_six\":\"西部院校协会\",\"four_top_pic\":\"\\/uploads\\/20190128\\/naae\\/c1d6bd6261426d7eccd08de51c481055.jpg\",\"four_right_pic\":\"\\/uploads\\/20190128\\/naae\\/25828f6bc5fdcf706ae708d14c3360ba.jpg\"}', 4, 1548642611);
INSERT INTO `clt_naae_media` VALUES (353, 63, '推荐理由·Recommended Rea', '{\"id\":\"\",\"pics_5\":\"[\\\"\\/uploads\\/20190128\\/naae\\/98228e2ceac14842cf0062b427e17047.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/f828ac2ef35b68e811b047ddc065bc56.jpg\\\"]\",\"page\":\"5\",\"title_catalog\":\"推荐理由·Recommended Reasons\",\"five_text\":\"\",\"content5\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校着重学生全方面发展，注重<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>体育和艺术方面<\\/strong><\\/span>的培养，获得无数区域和国家的<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>奖项，<\\/strong><\\/span>包括棕榈泉艺术博物馆的创造力和奖学金计划，全国艺术展，万斯文化比赛和国会艺术比赛奖项。学校也为学生提供了23种代表性运动项目等。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">为学生进入名牌大学做好充足准备，学校提供<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>AP课程20门，<\\/strong><\\/span>升学情况出色，学生升入美国和加拿大顶尖名校：哈佛大学，普林斯顿大学，耶鲁大学，芝加哥大学，多伦多大学，宾夕法尼亚大学，康奈尔大学，布朗大学等。.2018年，学生在大学申请中获得很出色的成绩，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>95%<\\/strong><\\/span>的学生获得了至少一项奖学金，共计超过<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>440万。<\\/strong><\\/span><br\\/><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校开设<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>商业特色课程，<\\/strong><\\/span>开设<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>STEM理工课程项目，<\\/strong><\\/span>多方位的培养学生的能力，充分调动学生的实践的积极性。<\\/span><\\/p>\"}', 5, 1548642805);
INSERT INTO `clt_naae_media` VALUES (354, 63, '学术特色·Academic Featur', '{\"id\":\"354\",\"pics_6\":\"[\\\"\\/uploads\\/20190128\\/naae\\/9c42fa4ef62e5f173dbb7c3514f6a71a.png\\\",\\\"\\/uploads\\/20190128\\/naae\\/de040eb228173a973c1466542326b1a5.jpg\\\"]\",\"page\":\"6\",\"title_catalog\":\"学术特色·Academic Features\",\"six_text\":\"\",\"content6\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">Niche调查中，学校学术评级大学升学预备均是<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>A级别，<\\/strong><\\/span>学术实力强。<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>学校SAT均分1160，ACT均分26，<\\/strong><\\/span>高于美国平均水平（全美平均SAT1080分，平均ACT23分），所有课程均为荣誉课程。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校有<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>个人咨询和大学心理咨询项目，<\\/strong><\\/span>鼓励、帮助和引导学生，有效地管理自己的时间和精力，实现自己的目标，提供最佳的申请大学的选择。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校开设<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(63, 63, 63);\\\"><strong>商业特色课程，<\\/strong><\\/span>通过商业理论和案例的教学， 开拓学生国际化视野，提高学生英文沟通能力及商业实践能力 。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校开始<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>STEM理工特色项目，<\\/strong><\\/span>组建<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>智能机器人俱乐部，<\\/strong><\\/span>结合科学、技术、工程、数学、艺术的教育内容，不但要求学生学习学科内容，还注重学生创新能力、批判性思维和合作能力的培养。&nbsp;<\\/span><\\/p>\"}', 6, 1548643006);
