INSERT INTO `clt_naae_media` VALUES (358, 63, '艺术特色·Fine Arts', '{\"id\":\"358\",\"pics_10\":\"[\\\"\\/uploads\\/20190128\\/naae\\/678a7eb53ba69f4e91ed24567db11b00.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/43bc643e05c7b7bdcd3c7d57f3340627.jpg\\\"]\",\"page\":\"10\",\"title_catalog\":\"艺术特色·Fine Arts\",\"ten_pic\":\"\\/uploads\\/20190128\\/naae\\/b68865c9f21577a14ca9cbdbf75215fb.jpg\",\"z_d_y_data\":\"{\\\"艺术特色\\\":\\\"沙维尔学校的音乐项目很好，有钢琴，摇滚，爵士，流行和古典等，并且学校和当地的一个叫True Vine的音乐学院有合作。钢琴是学校项目的一部分，费用包含在学费中，还有关于美术展览方面有国际生中国孩子参与展览比赛获奖等。\\\"}\"}', 10, 1548643946);
