INSERT INTO `clt_naae_media` VALUES (360, 63, '学校设施·Facilities', '{\"id\":\"\",\"pics_12\":\"[\\\"\\/uploads\\/20190128\\/naae\\/752fac17a7c9d9cc54ddfab47938d06d.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/e7e8ba4f15addb2f77e95b2d33340301.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/101ba503f0f2490fc26e8c70f7a65cd0.jpg\\\"]\",\"page\":\"12\",\"title_catalog\":\"学校设施·Facilities\",\"z_d_y_data\":\"{\\\"学校设施\\\":\\\"田径场，橄榄球场，棒球场，网球场。室内：篮球场，戏剧中心，图书馆，电子书籍，试听教室。\\\"}\"}', 12, 1548644105);
INSERT INTO `clt_naae_media` VALUES (361, 63, '大学升学情况·University-En', '{\"id\":\"\",\"pics_13\":\"\",\"page\":\"13\",\"title_catalog\":\"大学升学情况·University-Entrance Status\",\"thirteen_summary\":\"该校学生申请最多的大学如下：\",\"thirteen_pic_top\":\"\\/uploads\\/20190128\\/naae\\/698d02ed0feed8bb53710a7e5d2dd1e7.png\",\"thirteen_pic_low\":\"\\/uploads\\/20190128\\/naae\\/26bede953f9063724e7a0aaecdb8f738.jpg\"}', 13, 1548644196);
INSERT INTO `clt_naae_media` VALUES (362, 63, '社区概况·Community Profi', '{\"id\":\"\",\"pics_14\":\"[\\\"\\/uploads\\/20190128\\/naae\\/26c910a2f85c9fe3a24f75dc100a498d.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/439312efcd9b813927e3f470609a7f4d.jpg\\\"]\",\"page\":\"14\",\"title_catalog\":\"社区概况·Community Profile\",\"fourteen_text\":\"\",\"content14\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">棕榈泉美国加利福尼亚州南部的游览城市。在洛杉矶以东160公里、被雪覆盖的圣哈辛托山麓,借高架有轨电车(是美国最长和最高的高架有轨电车)可登攀。向有&quot;美国第一流沙漠游览地&quot;之称。有著名的高尔夫球场。按人口比例计算,本市的游泳池数量居世界首位。有1,500-2,000年古老的棕榈树及矿温泉,还有许多美术陈列馆 该社区环境安全，治安良好。<\\/span><\\/p>\"}', 14, 1548644392);
INSERT INTO `clt_naae_media` VALUES (363, 63, '反馈·Feedback', '{\"id\":\"363\",\"pics_15\":\"[\\\"\\/uploads\\/20190128\\/naae\\/d5d0c50819124e0161445cd6b9937503.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/f917b6643b73fab141e458d6cd8c443e.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/075e358355facdd7f87d17df67ed5de2.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/66ddf6efbd371014acc840fa83ce9c68.png\\\"]\",\"page\":\"15\",\"title_catalog\":\"反馈·Feedback\",\"fifteen_text\":\"\",\"fifteen_pic_top\":\"\\/uploads\\/20190128\\/naae\\/5661c443c41eedc36b9bbb6a9bcdc379.png\",\"fifteen_pic_low\":\"\\/uploads\\/20190128\\/naae\\/65f7bee3dbc2bb279c01f528034221de.JPG\",\"content15\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">在读学生及家长整体评价，其中四星及五星评价占比<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>71%。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"color: rgb(38, 38, 38);\\\"><strong><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">79%<\\/span><\\/strong><\\/span><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">的学生认为学校的同学很友好；<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>79%<\\/strong><\\/span>的学生认为该校的学生很积极参与各种活动；<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>84%<\\/strong><\\/span>的学生认为该校的学习氛围浓厚，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>79%<\\/strong><\\/span>的学生和家长认为该学校的社团和活动很丰富并且很有趣，学生们都会积极参与；<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>91%<\\/strong><\\/span>的学生和家长认为老师在课堂上讲课很生动，并且能有效控制课堂节奏；<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(38, 38, 38);\\\"><strong>86%<\\/strong><\\/span>的学生和家长认为老师对学生非常关心，关注学生学习和生活。<\\/span><\\/p>\"}', 15, 1548644664);
INSERT INTO `clt_naae_media` VALUES (364, 63, '学校费用·Tuition', '{\"id\":\"\",\"pics_16\":\"[\\\"\\/uploads\\/20190128\\/naae\\/b85d26e30b79a0a168106507ef71482a.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/d37ef7e940efaefccf23dcf92da77504.jpg\\\"]\",\"page\":\"16\",\"title_catalog\":\"学校费用·Tuition\",\"sixteen_two_level_title\":\"54000美金\\/年\",\"sixteen_summary\":\"该费用包括学费、寄宿费、餐费、管理费。此报价为预估费用，每年可能会有5%-10%的涨幅，具体以invoice为准。(此外学生还需额外缴纳注册费、国际生费，科技费,书费等其他杂费。）\"}', 16, 1548644722);
