INSERT INTO `clt_naae_media` VALUES (367, 64, '学校概况·Overview', '{\"id\":\"\",\"page\":\"2\",\"title_catalog\":\"学校概况·Overview\",\"two_pic\":\"\\/uploads\\/20190128\\/naae\\/6659d4c2b32cc94e79bb422dbf39ed58.png\",\"z_d_y_data\":\"{\\\"建校时间\\\":\\\"1993年\\\",\\\"学生人数\\\":\\\"236人\\\",\\\"占地面积\\\":\\\"20英亩\\\",\\\"宗教背景\\\":\\\"基督教\\\",\\\"周边城市\\\":\\\"匹兹堡、底特律\\\",\\\"学校性质\\\":\\\"男女合校\\\",\\\"年级设置\\\":\\\"K-12年级\\\",\\\"招生年级\\\":\\\"8-12年级\\\",\\\"住宿类型\\\":\\\"学校公寓\\\",\\\"师生比例\\\":\\\"1:14\\\",\\\"国际学生\\\":\\\"10%\\\",\\\"课堂规模\\\":\\\"20人\\\",\\\"高级教师\\\":\\\"75%教师拥有高等学历\\\",\\\"ESL课程\\\":\\\"有\\\",\\\"SAT均分\\\":\\\"1440\\\",\\\"AP课程\\\":\\\"在线28门\\\",\\\"入学要求\\\":\\\"TOEFL\\\",\\\"学校官网\\\":\\\"www.efcaonline.org\\\",\\\"申请费\\\":\\\"160美金\\\"}\"}', 2, 1548668712);
INSERT INTO `clt_naae_media` VALUES (368, 64, '学校排名·School Ranking', '{\"id\":\"\",\"pics_3\":\"[\\\"\\/uploads\\/20190128\\/naae\\/79db5b4226a60af5a9557ab5edfbc659.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/1ed54e9bf0f1f2330f02316e7c3b2abf.jpg\\\"]\",\"page\":\"3\",\"title_catalog\":\"学校排名·School Ranking\",\"ranking_one\":\"美国权威高中排名，Niche排名A类院校\",\"ranking_two\":\"全美私立基督教高中排名前20%\",\"ranking_three\":\"全美最多样化高中排名前10%\",\"three_top_pic\":\"\\/uploads\\/20190128\\/naae\\/ba37fd10af644ca58fabc2c9813d05ee.png\",\"three_low_pic\":\"\\/uploads\\/20190128\\/naae\\/652542036b31d6bf3855b45e09ae5868.png\"}', 3, 1548669133);
INSERT INTO `clt_naae_media` VALUES (369, 64, '学校认证·School Accredit', '{\"id\":\"\",\"pics_4\":\"[\\\"\\/uploads\\/20190128\\/naae\\/3e6ae895eb02fa8fc532999d7c36aade.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/275efddb6b6b2470ce2b6d8fff2528dc.jpg\\\"]\",\"page\":\"4\",\"title_catalog\":\"学校认证·School Accredition\",\"authentication_one\":\"东北地区优质基督学校之一\",\"authentication_two\":\"篮球队获得纽约-宾州联赛冠军\",\"authentication_three\":\"获得宾州基督联盟男子足球赛冠军(多次)\",\"authentication_four\":\"EFCA获有国际基督教学校联盟认证\",\"authentication_five\":\"美国中部地区院校联盟(MSA)的资格认证\",\"authentication_six\":\"东北地区优质基督学校之一\",\"four_top_pic\":\"\\/uploads\\/20190128\\/naae\\/42951bbb4bf65dd6babb68dc98b3c27b.jpg\",\"four_right_pic\":\"\\/uploads\\/20190128\\/naae\\/0551aebc60ab0644c4c0cd4b9e363d29.jpg\"}', 4, 1548669271);
INSERT INTO `clt_naae_media` VALUES (370, 64, '推荐理由·Recommended Rea', '{\"id\":\"370\",\"pics_5\":\"[\\\"\\/uploads\\/20190128\\/naae\\/90df37ddcaafa6f1af6907747001bdff.jpg\\\"]\",\"page\":\"5\",\"title_catalog\":\"推荐理由·Recommended Reasons\",\"five_text\":\"\",\"content5\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">伊利第一基督无愧宾夕法尼亚州<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>“明星寄宿高中”<\\/strong><\\/span>之称，位于最秉持纯正美国特色和美国精神的宾州，学校距五大湖之一的伊利湖畔只几分钟的路程，学生们可以享受到很多当地的<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>户外活动，<\\/strong><\\/span>如洞穴探险、软弹气枪、皮艇运动、钓鱼、射击、越野滑雪、童子军、激光枪战游戏、梦幻运动、骑术、高尔夫、攀岩运动、户外烧烤和宿营、校际运动、参加健身俱乐部等。学生们还有机会<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>参加前往纽约和首都华盛顿的实地考察旅行。<\\/strong><\\/span><\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校不仅不仅有高中阶段必修的基础学科，还有灵活自由，根据个人兴趣特长设置的各种选修课及大学预备课程。学校对运动和课外实践都非常重视，开设了大量的<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>特色课程，<\\/strong><\\/span>无论陶冶情操的绘画课、戏剧课、合唱团，还是注重领导力培养的<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>领导力发展项目、商业计算机课、社会学课等，<\\/strong><\\/span>都能使学生获得充分综合的发展机会。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校提供温馨的宿舍配套设施，每个房间都有<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>私人浴室<\\/strong><\\/span>和淋浴设施，<strong>洗衣设施设于宿舍内，<\\/strong>供寄宿生<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>免费使用，<\\/strong><\\/span>24小时热水，全天候Wi-Fi覆盖，全天候安全监控等。宿舍可以提供安静舒适的自习环境，让丰富多彩的宿舍生活伴随学生健康成长。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校擅长用<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>运动和健身<\\/strong><\\/span>项目来推动学生的积极性、创造性和主观能动性的发展，以此来建立个人自尊、自信的人生态度，和健康的体魄.学校提供全方位的有组织的运动项目，从基本的运动健身技巧，到高级的体重管理项目等，不论你学生属于哪一类体育运动爱好者，都能找到适合自己的课程。<\\/span><\\/p><p><br\\/><\\/p>\"}', 5, 1548669629);
INSERT INTO `clt_naae_media` VALUES (371, 64, '学术特色·Academic Featur', '{\"id\":\"371\",\"pics_6\":\"[\\\"\\/uploads\\/20190128\\/naae\\/d03f37f7676d1e92d9f902005099853a.jpg\\\"]\",\"page\":\"6\",\"title_catalog\":\"学术特色·Academic Features\",\"six_text\":\"\",\"content6\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">Niche调查中，，学校学术实力很强，<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>SAT均分1440，ACT34分，<\\/strong><\\/span>远高于美国平均水平（全美SAT均分1080，ACT均分23）。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校提供<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>私人辅导项目，<\\/strong><\\/span>在某一方面有有特殊天赋，有学习障碍，精英运动员可以选择专业的辅导老师进行私人辅导，更有健身和体重顾问。通过将学生与学术、运动和家庭导师紧紧结合，确保学生能接受到最适合自己的教育。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">伊利第一基督学校与<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>宾州州立大学为本校高中生开展特殊的合作项目,<\\/strong><\\/span>学生也可以在贝伦德学院学习两年后,转到宾州州立大学帕克分校学习,宾夕法尼亚州立大学是录取伊利第一基督中学学生最多的大学。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">灵活的课程设置，学生可以根据自己的学业压力、个人学习能力和未来本科专业及职业发展方向设计自己的课程，让自身优势和潜力得到充分发展。课程包括：<span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>36门基础课程；10门校内荣誉课程；10门艺术类课程（摄影，绘画，戏剧等）；<\\/strong><\\/span><span style=\\\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(23, 54, 93);\\\"><strong>28门在线AP课程；加努恩大学的RCI双学分课程，<\\/strong><\\/span>通过该课程的学习，可以缩短获得大学学士学位的时间，学校同时开设了领导力发展项目、商业计算机课等特色课程。<\\/span><\\/p>\"}', 6, 1548669820);
INSERT INTO `clt_naae_media` VALUES (372, 64, '课程设置·Curriculum', '{\"id\":\"\",\"page\":\"7\",\"title_catalog\":\"课程设置·Curriculum\",\"seven_pic\":\"\\/uploads\\/20190128\\/naae\\/0d2073c52332c0a6bfeb8ddcc64ed8b7.jpg\",\"z_d_y_data\":\"{\\\"圣经课程\\\":\\\"旧约~新约~教会历史\\\\\\/神学~护教\\\\\\/世界观\\\",\\\"社会研究课程\\\":\\\"美国历史~美国政府与政治~世界文化\\\",\\\"数学课程\\\":\\\"代数~几何~企业数学~三角函数~初级微积分~微积分\\\",\\\"科学课程\\\":\\\"生物学~物理学~化学~环境科学~解剖生理学\\\"}\"}', 7, 1548670590);
INSERT INTO `clt_naae_media` VALUES (373, 64, '课程设置·Curriculum', '{\"id\":\"\",\"page\":\"8\",\"title_catalog\":\"课程设置·Curriculum\",\"eight_pic\":\"\\/uploads\\/20190128\\/naae\\/e553987eaafe96f591cc2f418fea31c6.jpg\",\"z_d_y_data\":\"{\\\"选修课程\\\":\\\"摄影~绘画~艺术二~赞美乐队~合唱~戏剧~司机教育~金融~健身~电脑~逻辑~报纸~SAT预备~学校服务~西班牙语~年鉴~希腊历史~体育~健康~ESL\\\",\\\"荣誉课程\\\":\\\"初级微积分~微积分~化学I~化学II~解剖学~生理学~物理学\\\"}\"}', 8, 1548670776);
INSERT INTO `clt_naae_media` VALUES (374, 64, '体育特色·Sports Activiti', '{\"pics_9_top\":\"[\\\"\\/uploads\\/20190128\\/naae\\/9c108e7de50210bfffcdb74d2da35ae0.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/c0f329a449c49b3573c5b4d1462dfffd.jpg\\\"]\",\"pics_9_low\":\"[\\\"\\/uploads\\/20190128\\/naae\\/aad55379eca31bab1e1a2ed2bdd8548c.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/f7d5e78498c2ec92219174bb638f268b.jpg\\\"]\",\"id\":\"\",\"pics_9\":\"\",\"page\":\"9\",\"title_catalog\":\"体育特色·Sports Activities\",\"z_d_y_data\":\"{\\\"秋季运动\\\":\\\"足球，越野赛\\\",\\\"冬季运动\\\":\\\"男篮，女篮\\\",\\\"春季运动\\\":\\\"田径，学校拥有多样的校运动队，如：男子与女子篮球、英式足球、排球、美式橄榄球、网球、羽毛球等。每年组织学生参与校际及区域性质的联赛,在锻炼身体的同时，培养自己在体育运动方面的兴趣爱好。另外还可选择划船、观看体育赛事、健身俱乐部、高尔夫、骑行、排球、羽毛球、乒乓球、彩弹、滑雪、橄榄球、台球等活动。\\\"}\"}', 9, 1548670904);
INSERT INTO `clt_naae_media` VALUES (375, 64, '艺术特色·Fine Arts', '{\"id\":\"\",\"pics_10\":\"[\\\"\\/uploads\\/20190128\\/naae\\/2302997e29e755e47a8554d860e7322a.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/1b40c9cd5fdd1aceee6316132e554f17.jpg\\\"]\",\"page\":\"10\",\"title_catalog\":\"艺术特色·Fine Arts\",\"ten_pic\":\"\\/uploads\\/20190128\\/naae\\/81f8b4d451288b00b5142778d61410f6.jpg\",\"z_d_y_data\":\"{\\\"艺术类课程\\\":\\\"才艺表演、合唱、图形艺术、摄影、歌唱、绘画、音乐概论、艺术导论等。\\\"}\"}', 10, 1548670980);
INSERT INTO `clt_naae_media` VALUES (376, 64, '学生社团·Club Activities', '{\"id\":\"\",\"pics_11\":\"[\\\"\\/uploads\\/20190128\\/naae\\/f9ec71f55bc01173ae3865edef756bed.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/7bcf22d0d189f89d417e4a66433c6e73.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/a06b584ae66e96277f2a8893e443fc0a.jpg\\\"]\",\"page\":\"11\",\"title_catalog\":\"学生社团·Club Activities\",\"eleven_summary\":\"学生们可以享受到很多当地的户外活动，如洞穴探险、软弹气枪、皮艇运动、钓鱼、射击、越野滑雪、童子军、激光枪战游戏、商业俱乐部、演讲和辩论、梦幻运动、骑术、高尔夫、攀岩运动、户外烧烤和宿营、校际运动、参加健身俱乐部等。学生们还有机会参加前往纽约和首都华盛顿的实地考察旅行。此外还有游戏之夜、电影与零食之夜、划船、观看体育赛事、集体购物、志愿者服务、参观博物馆或历史遗迹、动物园参观、烹饪、教堂服务、保龄球、才艺表演、冬季运动、农场活动、骑行、国际象棋、机器人、乒乓球、年鉴、摄影。\"}', 11, 1548671024);
INSERT INTO `clt_naae_media` VALUES (377, 64, '学校设施·Facilities', '{\"id\":\"\",\"pics_12\":\"[\\\"\\/uploads\\/20190128\\/naae\\/62bdcd510d9352f0c3d62b78c216b383.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/edd2547eccfafbab0a326686d89fb24d.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/091ed56ce76edc1882b770e004aebf01.jpg\\\"]\",\"page\":\"12\",\"title_catalog\":\"学校设施·Facilities\",\"z_d_y_data\":\"{\\\"室内\\\":\\\"智能白板、多功能教学设备、电脑、投影仪、多媒体\\\",\\\"室外\\\":\\\"基础设施先进，EFCA斥巨资建设的学生活动中心“The Summit”楼为学生提供篮球、排球、羽毛球等多个运动设施；内设耗资百万美元的多媒体剧院，音响设备就耗资60多万美元；超现代化的设计室、艺术教室、科学实验室等；咖啡馆、健身房、图书馆、自习室以及配有可视化游戏系统的活动室等。\\\",\\\"\\\":\\\"宿舍均为双人间,配有1.5米宽的单人床,独立卫生间,24小时热水,全天候Wi-Fi覆盖，免费干洗设备等。宿舍区配有16台电视监控，不间断监控管理，保证学生安全。此外EFCA宿舍在学习方面配有课后辅导员辅导学生作业，在饮食方面每日新鲜水果及酸奶是必不可少的，可在厨房自己制作食物，在娱乐方面配有篮球场、电视、游戏机活动室、与宿舍一街之隔有宿营地、冲浪地、步行小道等。更为人性化的是：在除了暑假外的任何假期（如圣诞节等）都对学生开放，学生无需另寻住所。\\\"}\"}', 12, 1548671095);
INSERT INTO `clt_naae_media` VALUES (378, 64, '大学升学情况·University-En', '{\"id\":\"\",\"pics_13\":\"\",\"page\":\"13\",\"title_catalog\":\"大学升学情况·University-Entrance Status\",\"thirteen_summary\":\"100%学生被四年制大学录取，近五年毕国际学生升学率100%。该校的学生申请最多的大学有如下：\",\"thirteen_pic_top\":\"\\/uploads\\/20190128\\/naae\\/cf73a2530e0b831630a4a2a429b73bb4.png\",\"thirteen_pic_low\":\"\\/uploads\\/20190128\\/naae\\/53f5ddfa802bf236eb07ca25872c6653.jpg\"}', 13, 1548671173);
INSERT INTO `clt_naae_media` VALUES (379, 64, '社区概况·Community Profi', '{\"id\":\"\",\"pics_14\":\"[\\\"\\/uploads\\/20190128\\/naae\\/7a362957940213c294aa17e30a7e77ae.jpg\\\",\\\"\\/uploads\\/20190128\\/naae\\/5f9c7f6c51e7413812370f35c6b02052.jpg\\\"]\",\"page\":\"14\",\"title_catalog\":\"社区概况·Community Profile\",\"fourteen_text\":\"\",\"content14\":\"<p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">学校位于宾夕法尼亚州伊利市，距伊利湖畔只有几分钟的车程。学生课外可以享受湖边特色项目，如钓鱼、户外烧烤、野营等。学校周边基础设施极为发达，生活十分便利，东北方几十米处便是通往克利夫兰、匹兹堡等城市的79、90号高速公路，穿过高速公路1公里便是三大购物中心和大型超市，学校东北方500米有水上公园供学生课后娱乐。另外周边有很多世界各地的美食。周边有圣乔治天主教学校、詹姆斯威尔森中学、贝塞尔基督教学校、麦克道尔高中等学校，这些学校之间有着良好的竞争关系。学校距离伊利国际机场仅9公里，出行十分便捷。<\\/span><\\/p><p style=\\\"text-indent: 2em;\\\"><span style=\\\"font-family: 宋体, SimSun; font-size: 14px;\\\">全美最好的学区排名前20%,宾夕法尼亚州最好的学区排名前20%.<\\/span><\\/p>\"}', 14, 1548671375);
INSERT INTO `clt_naae_media` VALUES (380, 64, '反馈·Feedback', '{\"id\":\"\",\"pics_15\":\"[\\\"\\/uploads\\/20190128\\/naae\\/d801cc0c57dc6e28f40b948d92b55247.png\\\",\\\"\\/uploads\\/20190128\\/naae\\/f5419a3b56f8415b208627ce9bc1d7f7.png\\\"]\",\"page\":\"15\",\"title_catalog\":\"反馈·Feedback\",\"fifteen_text\":\"\",\"fifteen_pic_top\":\"\\/uploads\\/20190128\\/naae\\/a1a49c1155bec3ecaf9fd2b2045b74cc.png\",\"fifteen_pic_low\":\"\\/uploads\\/20190128\\/naae\\/3944788838a8b3f1b22d1bb307dbe3dd.png\",\"content15\":\"<p>在读学生及家长整体评价为<span style=\\\"color: rgb(23, 54, 93);\\\"><strong>五星级，<\\/strong><\\/span>其中五星评价占比<span style=\\\"color: rgb(23, 54, 93);\\\"><strong>94%。<\\/strong><\\/span><\\/p>\"}', 15, 1548671486);
